import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();
  const [userDetails, setUserDetails] = useState(null);
  const [loading, setLoading] = useState(true);

  // Fetch user details from the backend
  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const response = await fetch('/dashboard', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`  // Assuming you store the JWT in localStorage
          }
        });
        const data = await response.json();

        if (data.success) {
          setUserDetails(data.userDetails);
        } else {
          console.error('Failed to fetch user details');
        }
      } catch (error) {
        console.error('Error fetching user details:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchUserDetails();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/logout');
  };

  const handleDeleteAccount = () => {
    navigate('/delete-account');
  };

  return (
    <div className="dashboard-container">
      {/* Logo on the top-left corner */}
      <img src="/logo1.png" alt="Logo" className="logo-img" />
      <p className="logo-caption">University of Chittagong</p>

      {/* Profile Section */}
      <div className="profile-section">
        {loading ? (
          <p>Loading user details...</p>
        ) : (
          userDetails && (
            <div className="profile-info">
              <h2 className="username">{userDetails.userName}</h2>
              <p className="user-id">User ID: {userDetails.userId}</p>
              <p className="user-email">Email: {userDetails.userEmail}</p>
              <p className="user-role">Role: {userDetails.userRole}</p>
              
              <h3>Clubs:</h3>
              <ul>
                {userDetails.userClubs && userDetails.userClubs.length > 0 ? (
                  userDetails.userClubs.map((club, index) => (
                    <li key={index}>{club}</li>
                  ))
                ) : (
                  <li>No clubs found.</li>
                )}
              </ul>
              
              <h3>Events:</h3>
              <ul>
                {userDetails.userEvents && userDetails.userEvents.length > 0 ? (
                  userDetails.userEvents.map((event, index) => (
                    <li key={index}>{event}</li>
                  ))
                ) : (
                  <li>No events found.</li>
                )}
              </ul>
              
              <h3>Resources:</h3>
              <ul>
                {userDetails.userResources && userDetails.userResources.length > 0 ? (
                  userDetails.userResources.map((resource, index) => (
                    <li key={index}>{resource}</li>
                  ))
                ) : (
                  <li>No resources found.</li>
                )}
              </ul>

              {/* Delete Account Button */}
              <button className="btn btn-danger" onClick={handleDeleteAccount}>
                Delete Account
              </button>
            </div>
          )
        )}
      </div>

      {/* Logout Button */}
      <button className="btn btn-warning" onClick={handleLogout}>Logout</button>

      {/* Dynamic Button Group */}
      <div className="button-group">
        {/* Other dynamic buttons */}
      </div>
    </div>
  );
};

export default Dashboard;
