import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [message, setMessage] = useState('');
  const [role, setRole] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios
        .get('http://localhost:8000/dashboard', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          setMessage(response.data.message);
          setRole(response.data.role); // Assume the backend returns the user's role
        })
        .catch((error) => {
          setMessage('Access denied. Please log in.');
        });
    } else {
      setMessage('Access denied. Please log in.');
    }
  }, []);

  const renderContentByRole = () => {
    switch (role) {
      case 'admin':
        return <AdminSection />;
      case 'customer':
        return <CustomerSection />;
      case 'technician':
        return <TechnicianSection />;
      default:
        return <p>Please log in to access the dashboard.</p>;
    }
  };

  return (
    <div>
      <h1>Dashboard</h1>
      <p>{message}</p>
      {renderContentByRole()}
    </div>
  );
};

const AdminSection = () => (
  <div>
    <h2>Admin Panel</h2>
    <p>Welcome, Admin! Manage users and system settings here.</p>
  </div>
);

const CustomerSection = () => (
  <div>
    <h2>Customer Dashboard</h2>
    <p>Welcome, Customer! Browse and book services here.</p>
  </div>
);

const TechnicianSection = () => (
  <div>
    <h2>Technician Dashboard</h2>
    <p>Welcome, Technician! Update your location and view bookings here.</p>
  </div>
);

export default Dashboard;
