import React, { useState } from 'react';
import axios from 'axios';

function SearchTechnician() {
  const [location, setLocation] = useState('');
  const [technicians, setTechnicians] = useState([]);
  const [error, setError] = useState('');

  const searchTechnicians = async () => {
    if (!location.trim()) {
      setError('Please enter a location.');
      setTechnicians([]);
      return;
    }

    try {
      const response = await axios.get('http://localhost:8000/search-technicians', {
        params: { location },
      });

      if (response.data.success) {
        setTechnicians(response.data.technicians);
        setError('');
      } else {
        setTechnicians([]);
        setError('No technicians found in this location.');
      }
    } catch (err) {
      console.error(err);
      setTechnicians([]);
      setError('Failed to fetch technicians. Please try again later.');
    }
  };

  return (
    <div className="search-container">
      <h1>Search for Technicians</h1>
      <input
        type="text"
        placeholder="Enter location"
        value={location}
        onChange={(e) => setLocation(e.target.value)}
      />
      <button onClick={searchTechnicians}>Search</button>
      {error && <p className="error">{error}</p>}
      <div className="results">
        {technicians.map((tech) => (
          <div className="technician" key={tech.user_id}>
            <p><strong>Name:</strong> {tech.user_name}</p>
            <p><strong>Email:</strong> {tech.email}</p>
            <p><strong>Phone:</strong> {tech.phone_number}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default SearchTechnician;
