import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./AdminDashboard.css"; // For custom styling

function AdminDashboard() {
  const navigate = useNavigate();
  const [user, setUser] = useState({}); // To store user info (username, role)

  // Simulating getting user data
  useEffect(() => {
    const loggedInUser = JSON.parse(localStorage.getItem("user")); 
    setUser(loggedInUser || { username: "Admin", role: "Administrator" });
  }, []);

  // Navigation handlers
  const handleViewClubs = () => navigate('/clubs');
  const handleViewEvents = () => navigate('/events');
  const handleViewResources = () => navigate('/resources');
  const handleShareThoughts = () => navigate('/share-thought');
  const handleViewUsers = () => navigate('/all-users');
  const handleCreateClub = () => navigate('/create-club');
  const handleCreateEvent = () => navigate('/create-event');

  // Logout and Delete Account Handlers
  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  const handleDeleteAccount = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  return (
    <div className="dashboard">
      {/* Top Navigation Bar */}
      <div className="topbar">
        <div className="logo">Admin Dashboard</div>
        <div className="profile">
          <p>{user.username}</p>
          <p className="role">{user.role}</p>
        </div>
        <div className="actions">
          <button onClick={handleDeleteAccount} className="delete-btn">
            Delete Account
          </button>
          <button onClick={handleLogout} className="logout-btn">
            Logout
          </button>
        </div>
      </div>

      {/* Main Content */}
      <div className="content">
        <h1>Welcome to the Admin Dashboard</h1>
        <p>Manage everything effortlessly.</p>

        <div className="dashboard-actions">
          <button onClick={handleViewClubs} className="action-btn">
            Clubs
          </button>
          <button onClick={handleViewEvents} className="action-btn">
            Events
          </button>
          <button onClick={handleViewResources} className="action-btn">
            Resources
          </button>
          <button onClick={handleShareThoughts} className="action-btn">
            Share Thoughts
          </button>
          <button onClick={handleViewUsers} className="action-btn">
            View All Users
          </button>
        </div>

        <div className="admin-actions">
          <button onClick={handleCreateClub} className="action-btn admin-btn">
            Create Club
          </button>
          <button onClick={handleCreateEvent} className="action-btn admin-btn">
            Create Event
          </button>
        </div>
      </div>
    </div>
  );
}

export default AdminDashboard;
