import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ResourcesPage.css'; // Assuming you're using an external CSS file

const ResourcesPage = () => {
    const [resources, setResources] = useState([]);
    const [loading, setLoading] = useState(true);
    const [message, setMessage] = useState('');
    const [file, setFile] = useState(null);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [searchQuery, setSearchQuery] = useState(''); // Search state

    // Fetch resources from backend
    useEffect(() => {
        axios
            .get('http://localhost:8000/resources/all')
            .then((response) => {
                setResources(response.data.resources);
                setLoading(false);
            })
            .catch((err) => {
                console.error('Error fetching resources:', err);
                setMessage('Error loading resources.');
                setLoading(false);
            });
    }, []);

    // Filter resources based on search query
    const filteredResources = resources.filter(
        (resource) =>
            resource.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
            (resource.description && resource.description.toLowerCase().includes(searchQuery.toLowerCase()))
    );

    const handleUpload = async (e) => {
        e.preventDefault();

        if (!file || !title) {
            alert('Please provide a file and title.');
            return;
        }

        const formData = new FormData();
        formData.append('file', file);
        formData.append('title', title);
        formData.append('description', description);

        try {
            const response = await axios.post(
                'http://localhost:8000/resources/upload',
                formData,
                {
                    headers: {
                        'Content-Type': 'multipart/form-data',
                    },
                }
            );

            if (response.data.success) {
                alert('Resource uploaded successfully!');
                setResources([...resources, response.data.resource]);
            } else {
                alert('Failed to upload resource.');
            }
        } catch (error) {
            console.error('Upload error:', error.response || error.message);
            alert('An error occurred while uploading.');
        }
    };

    const handleDownload = (fileUrl) => {
        if (!fileUrl) {
            alert('File URL is missing.');
            return;
        }
        window.open(fileUrl, '_blank'); // Opens the file in a new tab
    };

    const handleShare = (fileUrl) => {
        navigator.clipboard.writeText(fileUrl)
            .then(() => alert('File URL copied to clipboard!'))
            .catch(() => alert('Failed to copy URL.'));
    };

    return (
        <div className="resources-page">
            {/* Left Side */}
            <div className="left-side">
                <input
                    type="text"
                    placeholder="Search resources..."
                    value={searchQuery}
                    onChange={(e) => setSearchQuery(e.target.value)}
                    className="search-bar"
                />

                <h3>Available Resources</h3>
                {loading ? (
                    <p>Loading...</p>
                ) : filteredResources.length === 0 ? (
                    <p>No resources found.</p>
                ) : (
                    <ul className="resources-list">
                        {filteredResources.map((resource) => (
                            <li className="resource-item" key={resource.resource_id}>
                                <h4>{resource.title}</h4>
                                <p>{resource.description}</p>
                                <div className="resource-actions">
                                    <button onClick={() => handleDownload(resource.file_url)}>
                                        Download
                                    </button>
                                    <button onClick={() => handleShare(resource.file_url)}>
                                        Share
                                    </button>
                                </div>
                            </li>
                        ))}
                    </ul>
                )}
            </div>

            {/* Right Side */}
            <div className="right-side">
                <h3>Upload a New Resource</h3>
                <form onSubmit={handleUpload} className="upload-form">
                    <input
                        type="text"
                        placeholder="Resource Title"
                        value={title}
                        onChange={(e) => setTitle(e.target.value)}
                        required
                    />
                    <textarea
                        placeholder="Description"
                        value={description}
                        onChange={(e) => setDescription(e.target.value)}
                    />
                    <input
                        type="file"
                        onChange={(e) => setFile(e.target.files[0])}
                        required
                    />
                    <button type="submit">Upload</button>
                </form>
            </div>
        </div>
    );
};

export default ResourcesPage;
