import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ResourcesPage.css'; // Assuming an external CSS file

const ResourcesPage = () => {
    const [resources, setResources] = useState([]);
    const [loading, setLoading] = useState(true);
    const [message, setMessage] = useState('');
    const [file, setFile] = useState(null);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [searchQuery, setSearchQuery] = useState('');

    // Fetch resources
    useEffect(() => {
        axios
            .get('http://localhost:8000/resources/all')
            .then((response) => {
                setResources(response.data.resources);
                setLoading(false);
            })
            .catch((err) => {
                console.error('Error fetching resources:', err);
                setMessage('Error loading resources.');
                setLoading(false);
            });
    }, []);

    // Filter resources
    const filteredResources = resources.filter(
        (resource) =>
            resource.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
            (resource.description &&
                resource.description.toLowerCase().includes(searchQuery.toLowerCase()))
    );

    // Handle upload
    const handleUpload = async (e) => {
        e.preventDefault();
        if (!file || !title) {
            alert('Please provide a file and title.');
            return;
        }

        const formData = new FormData();
        formData.append('file', file);
        formData.append('title', title);
        formData.append('description', description);

        try {
            const response = await axios.post(
                'http://localhost:8000/resources/upload',
                formData,
                {
                    headers: { 'Content-Type': 'multipart/form-data' },
                }
            );
            if (response.data.success) {
                alert('Resource uploaded successfully!');
                setResources([...resources, response.data.resource]);
            } else {
                alert('Failed to upload resource.');
            }
        } catch (error) {
            console.error('Upload error:', error.response || error.message);
            alert('An error occurred while uploading.');
        }
    };

    return (
        <div className="resources-container">
            {/* Navbar */}
            <header className="navbar">
                <div className="logo">ðŸ“š ResourceHub</div>
                <nav>
                    <a href="#">Dashboard</a>
                    <a href="#">Resources</a>
                    <a href="#">Profile</a>
                </nav>
            </header>

            <div className="content">
                {/* Left Side */}
                <aside className="sidebar">
                    <input
                        type="text"
                        placeholder="Search resources..."
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        className="search-bar"
                    />
                    <h3>Available Resources</h3>
                    {loading ? (
                        <p>Loading...</p>
                    ) : filteredResources.length === 0 ? (
                        <p>No resources found.</p>
                    ) : (
                        <ul className="resources-list">
                            {filteredResources.map((resource) => (
                                <li key={resource.resource_id} className="resource-card">
                                    <h4>{resource.title}</h4>
                                    <p>{resource.description}</p>
                                    <div className="actions">
                                        <button onClick={() => window.open(resource.file_url, '_blank')}>
                                            ðŸ“¥ Download
                                        </button>
                                        <button
                                            onClick={() =>
                                                navigator.clipboard.writeText(resource.file_url)
                                            }
                                        >
                                            ðŸ”— Share
                                        </button>
                                    </div>
                                </li>
                            ))}
                        </ul>
                    )}
                </aside>

                {/* Right Side */}
                <main className="upload-section">
                    <h3>Upload a New Resource</h3>
                    <form onSubmit={handleUpload} className="upload-form">
                        <input
                            type="text"
                            placeholder="Resource Title"
                            value={title}
                            onChange={(e) => setTitle(e.target.value)}
                            required
                        />
                        <textarea
                            placeholder="Description"
                            value={description}
                            onChange={(e) => setDescription(e.target.value)}
                        />
                        <input type="file" onChange={(e) => setFile(e.target.files[0])} required />
                        <button type="submit">Upload</button>
                    </form>
                </main>
            </div>
        </div>
    );
};

export default ResourcesPage;
