import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ClubsPage = () => {
  const [clubs, setClubs] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios
        .get('http://localhost:8000/clubs', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          // Assuming the response contains an array of clubs under `data.clubs`
          if (response.data.clubs) {
            setClubs(response.data.clubs);
          } else {
            setMessage('No clubs found.');
          }
        })
        .catch((err) => {
          console.log(err);
          setMessage('Error loading clubs. Please try again later.');
        });
    } else {
      setMessage('Access denied. Please log in.');
    }
  }, []);

  return (
    <div>
      <h1>All Clubs</h1>
      {message && <p>{message}</p>}
      
      {/* If clubs are available, map them */}
      {clubs.length > 0 ? (
        <ul>
          {clubs.map((club) => (
            <li key={club.club_id}>
              <strong>{club.club_name}</strong>
              <p>{club.description}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No clubs available.</p>
      )}
    </div>
  );
};

export default ClubsPage;
