import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ShareThought.css'; // Assuming you have a CSS file

const ShareThought = () => {
  const [thought, setThought] = useState('');
  const [photo, setPhoto] = useState(null);
  const [video, setVideo] = useState(null);
  const [thoughts, setThoughts] = useState([]);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  // Fetch all thoughts on component mount
  useEffect(() => {
    axios.get('http://localhost:8000/thoughts')
      .then(response => setThoughts(response.data.thoughts))
      .catch(error => setMessage('Failed to load thoughts.'));
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    const formData = new FormData();
    formData.append('thought', thought);
    if (photo) formData.append('photo', photo);
    if (video) formData.append('video', video);

    try {
      const response = await axios.post('http://localhost:8000/share-thought', formData, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'multipart/form-data',
        },
      });

      setMessage(response.data.message);
      setThought('');
      setPhoto(null);
      setVideo(null);
      setLoading(false);

      // Reload thoughts after sharing
      const newThoughts = await axios.get('http://localhost:8000/thoughts');
      setThoughts(newThoughts.data.thoughts);
    } catch (error) {
      console.error('Error sharing thought:', error);
      setMessage('Failed to share your thought.');
      setLoading(false);
    }
  };

  const handleFileChange = (e) => {
    const { name, files } = e.target;
    if (name === 'photo') {
      setPhoto(files[0]);
    } else if (name === 'video') {
      setVideo(files[0]);
    }
  };

  return (
    <div className="share-thought-container">
      <h2>Share Your Creative Thought</h2>
      {message && <p className="message">{message}</p>}
      <form onSubmit={handleSubmit}>
        <textarea
          name="thought"
          value={thought}
          onChange={(e) => setThought(e.target.value)}
          placeholder="Share your thought here..."
          rows="5"
          required
        />
        <input type="file" name="photo" accept="image/*" onChange={handleFileChange} />
        <input type="file" name="video" accept="video/*" onChange={handleFileChange} />
        <button type="submit" disabled={loading}>{loading ? 'Sharing...' : 'Share Thought'}</button>
      </form>

      <h3>All Thoughts</h3>
      <div className="thoughts-list">
        {thoughts.map((thought) => (
          <div key={thought.id} className="thought-item">
            <p><strong>{thought.name}</strong> shared:</p>
            <p>{thought.thought}</p>
            {thought.photo && <img src={`http://localhost:8000/${thought.photo}`} alt="Thought" />}
            {thought.video && <video controls src={`http://localhost:8000/${thought.video}`} />}
            <p>{new Date(thought.created_at).toLocaleString()}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default ShareThought;
