import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import './ClubDetailsPage.css';  // Your custom styles

const ClubDetailsPage = () => {
  const [clubDetails, setClubDetails] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [isMember, setIsMember] = useState(false); // Check if user is already a member
  const { clubId } = useParams();  // Get club ID from URL

  useEffect(() => {
    // Fetch club details by clubId
    axios
      .get(`http://localhost:8000/clubs/${clubId}`)
      .then((response) => {
        setClubDetails(response.data.club);
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching club details:', err);
        setError('Error loading club details. Please try again later.');
        setLoading(false);
      });
  }, [clubId]);

  // Handle join club action
  const handleJoinClub = () => {
    axios
      .post('http://localhost:8000/club/join', { club_id: clubId })
      .then((response) => {
        alert('Successfully joined the club!');
        setIsMember(true);
      })
      .catch((err) => {
        alert('Error joining the club. Please try again later.');
        console.error('Join club error:', err);
      });
  };

  return (
    <div className="club-details-page">
      <h1 className="page-title">Club Details</h1>
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>{error}</p>
      ) : clubDetails ? (
        <div className="club-details-container">
          <h2>{clubDetails.club_name}</h2>
          <p>{clubDetails.description}</p>
          <p><strong>Started On:</strong> {clubDetails.creation_date}</p>
          <p><strong>Total Members:</strong> {clubDetails.total_members}</p>
          {isMember ? (
            <p>You are already a member of this club.</p>
          ) : (
            <button className="join-club-btn" onClick={handleJoinClub}>
              Join Club
            </button>
          )}
        </div>
      ) : (
        <p>Club not found.</p>
      )}
    </div>
  );
};

export default ClubDetailsPage;
