import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ResourcesPage = () => {
    const [resources, setResources] = useState([]);
    const [loading, setLoading] = useState(true);
    const [message, setMessage] = useState('');
    const [file, setFile] = useState(null);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [userId] = useState(1); // Replace with dynamic user_id from auth or context
    const [username] = useState('testUser'); // Replace with actual username

    useEffect(() => {
        axios
            .get('http://localhost:8000/resources/all')
            .then((response) => {
                setResources(response.data.resources);
                setLoading(false);
            })
            .catch((err) => {
                console.error('Error fetching resources:', err);
                setMessage('Error loading resources.');
                setLoading(false);
            });
    }, []);

    const handleUpload = async (e) => {
        e.preventDefault();

        if (!file || !title) {
            alert('Please provide a file and title.');
            return;
        }

        const formData = new FormData();
        formData.append('file', file);
        formData.append('title', title);
        formData.append('description', description);
        formData.append('user_id', userId);
        formData.append('username', username);

        try {
            const response = await axios.post(
                'http://localhost:8000/resources/upload',
                formData,
                {
                    headers: {
                        'Content-Type': 'multipart/form-data',
                    },
                }
            );

            if (response.data.success) {
                alert('Resource uploaded successfully!');
                setResources([...resources, response.data.resource]);
            } else {
                alert('Failed to upload resource.');
            }
        } catch (error) {
            console.error('Upload error:', error.response || error.message);
            alert('An error occurred while uploading.');
        }
    };

    const handleDownload = (fileUrl) => {
        window.open(fileUrl, '_blank');
    };

    const handleShare = (fileUrl) => {
        navigator.clipboard.writeText(fileUrl)
            .then(() => alert('File URL copied to clipboard!'))
            .catch(() => alert('Failed to copy URL.'));
    };

    return (
        <div>
            <h1>Resources</h1>
            <form onSubmit={handleUpload}>
                <input
                    type="text"
                    placeholder="Resource Title"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    required
                />
                <textarea
                    placeholder="Description"
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                />
                <input
                    type="file"
                    onChange={(e) => setFile(e.target.files[0])}
                    required
                />
                <button type="submit">Upload</button>
            </form>

            {loading && <p>Loading...</p>}
            {message && <p>{message}</p>}
            <ul>
                {resources.map((resource) => (
                    <li key={resource.resource_id}>
                        <h3>{resource.title}</h3>
                        <p>{resource.description}</p>
                        <button onClick={() => handleDownload(resource.file_url)}>
                            Download
                        </button>
                        <button onClick={() => handleShare(resource.file_url)}>
                            Share
                        </button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default ResourcesPage;
