import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ResourcesPage = () => {
  const [resources, setResources] = useState([]);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (token) {
      axios
        .get('http://localhost:8000/resources/all', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          setResources(response.data.resources);
          setLoading(false);
        })
        .catch((err) => {
          console.error(err);
          setMessage('Error loading resources.');
          setLoading(false);
        });
    } else {
      setMessage('Access denied. Please log in.');
      setLoading(false);
    }
  }, []);

  const handleShare = (fileUrl) => {
    navigator.clipboard.writeText(fileUrl).then(() => {
      alert('File URL copied to clipboard! Share it with others.');
    }).catch(() => {
      alert('Failed to copy file URL. Try again.');
    });
  };

  return (
    <div>
      <h1>All Resources</h1>
      {loading && <p>Loading resources...</p>}
      {message && <p>{message}</p>}
      <ul>
        {resources.length > 0 ? (
          resources.map((resource) => (
            <li key={resource.resource_id}>
              <strong>{resource.title}</strong>
              <p>Type: {resource.type}</p>
              <p>Uploaded on: {new Date(resource.upload_date).toLocaleString()}</p>
              {resource.file_url && (
                <div>
                  <a href={resource.file_url} download>
                    Download File
                  </a>
                  <button onClick={() => handleShare(resource.file_url)}>
                    Share File
                  </button>
                </div>
              )}
            </li>
          ))
        ) : (
          <p>No resources available.</p>
        )}
      </ul>
    </div>
  );
};

export default ResourcesPage;
