import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const ClubDetails = () => {
  const { id } = useParams(); // Get club ID from the route
  const [club, setClub] = useState(null);

  useEffect(() => {
    // Fetch club details
    axios.get(`http://localhost:8000/clubs/${id}`) // Replace with your backend route
      .then(response => setClub(response.data))
      .catch(error => console.error('Error fetching club details:', error));
  }, [id]);

  const joinClub = () => {
    axios.post(`http://localhost:8000/clubs/${id}/join`, {}, { withCredentials: true })
      .then(() => alert('You have successfully joined the club!'))
      .catch(error => console.error('Error joining club:', error));
  };

  if (!club) return <p>Loading...</p>;

  return (
    <div>
      <h1>{club.name}</h1>
      <p>{club.description}</p>
      <button onClick={joinClub}>Join Club</button>
    </div>
  );
};

export default ClubDetails;
