import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css'; // Assuming your CSS will be in this file

const Dashboard = () => {
  const navigate = useNavigate();
  
  // Assume user data comes from a global state or from a previous page (like the login page)
  const [userDetails, setUserDetails] = useState(null);

  // Fetch user data from localStorage or another source (this is just an example)
  useEffect(() => {
    const storedUserDetails = JSON.parse(localStorage.getItem('userDetails'));
    if (storedUserDetails) {
      setUserDetails(storedUserDetails);
    }
  }, []);

  // Handle button clicks to navigate to the appropriate page
  const handleViewClubs = () => {
    navigate('/clubs');
  };

  const handleViewEvents = () => {
    navigate('/events');
  };

  const handleViewResources = () => {
    navigate('/resources');
  };

  // Navigate to delete account page
  const handleDeleteAccount = () => {
    navigate('/delete-account');
  };

  // Navigate to logout page
  const handleLogout = () => {
    localStorage.removeItem('userDetails');  // Clear the user details from localStorage (or your state management)
    navigate('/logout');
  };

  return (
    <div className="dashboard-container">
      {/* Logo on the top left */}
      <div className="logo">
        <img src="logo1.png" alt="Logo" className="logo-img" />
      </div>

      {/* Profile Section on the top right */}
      <div className="profile-section">
        {userDetails ? (
          <div className="profile-info">
            <div className="user-details">
              <h2 className="username">{userDetails.name}</h2>
              <p className="user-role">{userDetails.role}</p>
              <button className="btn btn-info">View Profile</button>
            </div>
          </div>
        ) : (
          <p>Loading user details...</p>
        )}
        {/* Logout and Delete buttons */}
        <div className="top-right-buttons">
          <button className="btn btn-danger" onClick={handleDeleteAccount}>
            Delete Account
          </button>
          <button className="btn btn-warning" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </div>

      <h1 className="dashboard-title">Welcome to the digital frontier – it’s time to explore</h1>

      <div className="button-group">
        <div className="card">
          <button className="btn btn-primary" onClick={handleViewClubs}>View All Clubs</button>
        </div>
        <div className="card">
          <button className="btn btn-secondary" onClick={handleViewEvents}>View All Events</button>
        </div>
        <div className="card">
          <button className="btn btn-success" onClick={handleViewResources}>View All Resources</button>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
