import React, { useState, useEffect } from "react";
import axios from "axios";
import "./CreateEvent.css";

function CreateEvent() {
  const [eventData, setEventData] = useState({
    event_name: "",
    club_id: "",
    event_date: "",
    event_description: "",
    location: "",
    cover_photo: null,
    video: null,
  });

  const [events, setEvents] = useState([]); // To store the list of available events

  useEffect(() => {
    // Fetch events from the server when the component mounts
    const fetchEvents = async () => {
      try {
        const response = await axios.get("http://localhost:8000/get-events", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        });
        setEvents(response.data.events); // Assuming the response contains a "events" field
      } catch (error) {
        alert("Failed to load events");
      }
    };

    fetchEvents();
  }, []);

  const handleChange = (e) => {
    const { name, value, files } = e.target;

    if (name === "cover_photo" || name === "video") {
      setEventData({ ...eventData, [name]: files[0] });
    } else {
      setEventData({ ...eventData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("event_name", eventData.event_name);
    formData.append("club_id", eventData.club_id);
    formData.append("event_date", eventData.event_date);
    formData.append("event_description", eventData.event_description);
    formData.append("location", eventData.location);

    if (eventData.cover_photo) formData.append("cover_photo", eventData.cover_photo);
    if (eventData.video) formData.append("video", eventData.video);

    try {
      const response = await axios.post("http://localhost:8000/create-event", formData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
          "Content-Type": "multipart/form-data",
        },
      });
      alert(response.data.message);
    } catch (error) {
      alert(error.response?.data?.error || "Failed to create event");
    }
  };

  return (
    <div className="create-event-container">
      {/* Left side: List of Events */}
      <div className="event-list">
        <h3>Available Events</h3>
        <ul>
          {events.map((event) => (
            <li key={event.id}>
              <h4>{event.event_name}</h4>
              <p>{new Date(event.event_date).toLocaleDateString()}</p>
              <p>{event.location}</p>
              <p>{event.event_description}</p>
              {event.cover_photo && <img src={event.cover_photo} alt="Event Cover" />}
              {event.video && <video src={event.video} controls />}
            </li>
          ))}
        </ul>
      </div>

      {/* Right side: Create Event Form */}
      <div className="create-event-form">
        <h2>Create Event</h2>
        <form onSubmit={handleSubmit}>
          <input
            name="event_name"
            type="text"
            placeholder="Event Name"
            value={eventData.event_name}
            onChange={handleChange}
          />
          <input
            name="club_id"
            type="text"
            placeholder="Club ID"
            value={eventData.club_id}
            onChange={handleChange}
          />
          <input
            name="event_date"
            type="date"
            value={eventData.event_date}
            onChange={handleChange}
          />
          <textarea
            name="event_description"
            placeholder="Event Description"
            value={eventData.event_description}
            onChange={handleChange}
          />
          <input
            name="location"
            type="text"
            placeholder="Location"
            value={eventData.location}
            onChange={handleChange}
          />
          <div>
            <label>Event Cover Photo</label>
            <input
              name="cover_photo"
              type="file"
              accept="image/*"
              onChange={handleChange}
            />
          </div>
          <div>
            <label>Event Video</label>
            <input
              name="video"
              type="file"
              accept="video/*"
              onChange={handleChange}
            />
          </div>
          <button type="submit">Create Event</button>
        </form>
      </div>
    </div>
  );
}

export default CreateEvent;
