import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ResourcesPage = () => {
  const [resources, setResources] = useState([]);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState('');
  const [uploadMessage, setUploadMessage] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');

    if (token) {
      axios
        .get('http://localhost:8000/resources/all', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          setResources(response.data.resources);
          setLoading(false);
        })
        .catch((err) => {
          console.error(err);
          setMessage('Error loading resources.');
          setLoading(false);
        });
    } else {
      setMessage('Access denied. Please log in.');
      setLoading(false);
    }
  }, []);

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };

  const handleFileUpload = (e) => {
    e.preventDefault();
    const token = localStorage.getItem('token');

    if (!file || !title) {
      setUploadMessage('Please select a file and enter a title.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);
    formData.append('title', title);

    if (token) {
      axios
        .post('http://localhost:8000/resources/upload', formData, {
          headers: {
            Authorization: `Bearer ${token}`,
            'Content-Type': 'multipart/form-data',
          },
        })
        .then((response) => {
          setUploadMessage('File uploaded successfully!');
          setResources((prev) => [...prev, response.data.resource]); // Add new resource
          setFile(null);
          setTitle('');
        })
        .catch((err) => {
          console.error(err);
          setUploadMessage('Failed to upload file.');
        });
    } else {
      setUploadMessage('Access denied. Please log in.');
    }
  };

  return (
    <div>
      <h1>All Resources</h1>
      {loading && <p>Loading resources...</p>}
      {message && <p>{message}</p>}

      {/* File Upload Section */}
      <form onSubmit={handleFileUpload}>
        <div>
          <label htmlFor="title">Title: </label>
          <input
            type="text"
            id="title"
            value={title}
            onChange={handleTitleChange}
          />
        </div>
        <div>
          <label htmlFor="file">File: </label>
          <input type="file" id="file" onChange={handleFileChange} />
        </div>
        <button type="submit">Upload</button>
      </form>
      {uploadMessage && <p>{uploadMessage}</p>}

      {/* Resources List */}
      <ul>
        {resources.length > 0 ? (
          resources.map((resource) => (
            <li key={resource.resource_id}>
              <strong>{resource.title}</strong>
              <p>Type: {resource.type}</p>
              <p>Uploaded on: {new Date(resource.upload_date).toLocaleString()}</p>
            </li>
          ))
        ) : (
          <p>No resources available.</p>
        )}
      </ul>
    </div>
  );
};

export default ResourcesPage;
