import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ResourcesPage = () => {
  const [resources, setResources] = useState([]);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios
        .get('http://localhost:8000/resources/all', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          setResources(response.data.resources);
          setLoading(false);
        })
        .catch((err) => {
          console.error(err);
          setMessage('Error loading resources.');
          setLoading(false);
        });
    } else {
      setMessage('Access denied. Please log in.');
      setLoading(false);
    }
  }, []);

  const handleUpload = async (e) => {
    e.preventDefault();
    if (!file || !title) {
      alert('Please provide a file and a title.');
      return;
    }

    const token = localStorage.getItem('token');
    const formData = new FormData();
    formData.append('file', file);
    formData.append('title', title);
    formData.append('description', description);

    try {
      const response = await axios.post('http://localhost:8000/resources/upload', formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
          Authorization: `Bearer ${token}`,
        },
      });

      if (response.data.success) {
        alert('Resource uploaded successfully!');
        setResources([...resources, response.data.resource]); // Add the new resource to the list
      } else {
        alert('Failed to upload resource.');
      }
    } catch (error) {
      console.error(error);
      alert('An error occurred while uploading the resource.');
    }
  };

  const handleShare = (fileUrl) => {
    navigator.clipboard.writeText(fileUrl)
      .then(() => {
        alert('File URL copied to clipboard! Share it with others.');
      })
      .catch(() => {
        alert('Failed to copy file URL. Try again.');
      });
  };

  return (
    <div style={{ maxWidth: '1200px', margin: '0 auto', padding: '2rem' }}>
      <h1 style={{ textAlign: 'center', marginBottom: '2rem' }}>All Resources</h1>

      {/* Upload Resource Form */}
      <div style={{ marginBottom: '2rem', borderBottom: '2px solid #eee', paddingBottom: '1rem' }}>
        <h2 style={{ textAlign: 'center' }}>Upload New Resource</h2>
        <form onSubmit={handleUpload} style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
          <input
            type="text"
            placeholder="Resource Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            style={inputStyle}
          />
          <textarea
            placeholder="Description (optional)"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            style={{ ...inputStyle, height: '100px' }}
          />
          <input
            type="file"
            onChange={(e) => setFile(e.target.files[0])}
            required
            style={{ marginTop: '1rem' }}
          />
          <button type="submit" style={buttonStyle}>Upload</button>
        </form>
      </div>

      {/* Display Resources */}
      {loading && <p style={{ textAlign: 'center' }}>Loading resources...</p>}
      {message && <p style={{ color: 'red', textAlign: 'center' }}>{message}</p>}
      <ul style={{ listStyle: 'none', padding: '0' }}>
        {resources.length > 0 ? (
          resources.map((resource) => (
            <li key={resource.resource_id} style={{ marginBottom: '2rem', padding: '1rem', border: '1px solid #ddd', borderRadius: '8px' }}>
              <strong style={{ fontSize: '1.2rem' }}>{resource.title}</strong>
              <p style={{ fontStyle: 'italic', color: '#777' }}>Type: {resource.type}</p>
              <p style={{ color: '#555' }}>Uploaded on: {new Date(resource.upload_date).toLocaleString()}</p>
              <p>{resource.description}</p>
              {resource.file_url && (
                <div style={{ marginTop: '1rem' }}>
                  <a href={resource.file_url} download style={linkStyle}>
                    Download File
                  </a>
                  <button onClick={() => handleShare(resource.file_url)} style={{ ...buttonStyle, marginLeft: '1rem' }}>
                    Share File
                  </button>
                </div>
              )}
            </li>
          ))
        ) : (
          <p style={{ textAlign: 'center' }}>No resources available.</p>
        )}
      </ul>
    </div>
  );
};

// Inline styles
const inputStyle = {
  width: '100%',
  maxWidth: '500px',
  padding: '0.8rem',
  marginBottom: '1rem',
  borderRadius: '4px',
  border: '1px solid #ddd',
  fontSize: '1rem',
};

const buttonStyle = {
  padding: '0.8rem 2rem',
  backgroundColor: '#4CAF50',
  color: 'white',
  border: 'none',
  borderRadius: '4px',
  cursor: 'pointer',
  fontSize: '1rem',
  transition: 'background-color 0.3s ease',
};

const linkStyle = {
  color: '#007BFF',
  textDecoration: 'none',
  fontWeight: 'bold',
  fontSize: '1rem',
};

export default ResourcesPage;
