import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ClubsPage = () => {
  const [clubs, setClubs] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    // Fetch the clubs
    axios
      .get('http://localhost:8000/clubs/all') // Using the correct endpoint
      .then((response) => {
        console.log(response.data); // Check the API response
        if (response.data.clubs && Array.isArray(response.data.clubs)) {
          setClubs(response.data.clubs);
        } else {
          setMessage('No clubs found.');
        }
      })
      .catch((err) => {
        console.error('Error fetching clubs:', err);
        setMessage('Error loading clubs. Please try again later.');
      });
  }, []);

  return (
    <div>
      <h1>All Clubs</h1>
      {message && <p>{message}</p>}

      {/* Display clubs */}
      {clubs.length > 0 ? (
        <ul>
          {clubs.map((club) => (
            <li key={club.club_id}>
              <strong>{club.club_name}</strong>
              <p>{club.description}</p>
            </li>
          ))}
        </ul>
      ) : (
        <p>No clubs available.</p>
      )}
    </div>
  );
};

export default ClubsPage;
