import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();

  // State to store user details
  const [userDetails, setUserDetails] = useState(null);

  // Fetch user details from localStorage
  useEffect(() => {
    const storedUserDetails = JSON.parse(localStorage.getItem('userDetails'));
    if (storedUserDetails) {
      setUserDetails(storedUserDetails);
    }
  }, []);

  // Button navigation handlers
  const handleDeleteAccount = () => navigate('/delete-account');
  const handleLogout = () => {
    localStorage.removeItem('userDetails');
    navigate('/logout');
  };

  return (
    <div className="dashboard-container">
      {/* Logo on the top-left corner */}
      <img src="/logo1.png" alt="Logo" className="logo-img" />
      <p className="logo-caption">University of Chittagong</p>

      {/* Profile Section */}
      <div className="profile-section">
        {userDetails ? (
          <div className="profile-info">
            <h2 className="username">{userDetails.userName}</h2>
            <p className="user-id"><strong>User ID:</strong> {userDetails.userId}</p>
            <p className="user-email"><strong>Email:</strong> {userDetails.userEmail}</p>
            <p className="user-phone"><strong>Phone:</strong> {userDetails.userPhone}</p>
            <p className="user-role"><strong>Role:</strong> {userDetails.role}</p>

            <h3>Clubs:</h3>
            {userDetails.userClubs && userDetails.userClubs.length > 0 ? (
              <ul>
                {userDetails.userClubs.map((club, index) => (
                  <li key={index}>{club}</li>
                ))}
              </ul>
            ) : (
              <p>No clubs found.</p>
            )}

            <h3>Events:</h3>
            {userDetails.userEvents && userDetails.userEvents.length > 0 ? (
              <ul>
                {userDetails.userEvents.map((event, index) => (
                  <li key={index}>{event}</li>
                ))}
              </ul>
            ) : (
              <p>No events found.</p>
            )}

            <h3>Resources:</h3>
            {userDetails.userResources && userDetails.userResources.length > 0 ? (
              <ul>
                {userDetails.userResources.map((resource, index) => (
                  <li key={index}>{resource}</li>
                ))}
              </ul>
            ) : (
              <p>No resources found.</p>
            )}

            {/* Delete Account Button */}
            <div className="delete-account-section">
              <button className="btn btn-danger" onClick={handleDeleteAccount}>
                Delete Account
              </button>
            </div>
          </div>
        ) : (
          <p>Loading user details...</p>
        )}
      </div>

      {/* Logout button */}
      <div className="logout-button">
        <button className="btn btn-warning" onClick={handleLogout}>
          Logout
        </button>
      </div>
    </div>
  );
};

export default Dashboard;
