import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ClubsPage.css';  // Make sure to add styles in an external CSS file or inline styles

const ClubsPage = () => {
  const [clubs, setClubs] = useState([]);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true); // Added loading state

  useEffect(() => {
    // Fetch the clubs
    axios
      .get('http://localhost:8000/clubs/all') // Correct endpoint
      .then((response) => {
        console.log(response.data); // Check the API response
        if (response.data.clubs && Array.isArray(response.data.clubs)) {
          setClubs(response.data.clubs);
        } else {
          setMessage('No clubs found.');
        }
        setLoading(false); // Stop loading once data is fetched
      })
      .catch((err) => {
        console.error('Error fetching clubs:', err);
        setMessage('Error loading clubs. Please try again later.');
        setLoading(false); // Stop loading even on error
      });
  }, []);

  return (
    <div className="clubs-page">
      <h1>All Clubs</h1>
      {loading ? (
        <p>Loading clubs...</p> // Show loading text
      ) : message ? (
        <p className="error-message">{message}</p> // Display error message
      ) : clubs.length === 0 ? (
        <p>No clubs available.</p>
      ) : (
        <div className="clubs-container">
          {clubs.map((club) => (
            <div className="club-card" key={club.club_id}>
              <h3>{club.club_name}</h3>
              <p>{club.description}</p>
              {/* Optionally, add more details here, like club type, members, etc. */}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ClubsPage;
