import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import styles from "../styles/Dashboard.module.css"; // CSS Module for styling

const Dashboard = () => {
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("token");
    if (token) {
      axios
        .get("http://localhost:8000/dashboard", {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          setMessage(response.data.message);
        })
        .catch(() => {
          setMessage("Access denied. Please log in.");
        });
    } else {
      setMessage("Access denied. Please log in.");
    }
  }, []);

  // Delete Account Function
  const handleDeleteAccount = () => {
    const token = localStorage.getItem("token");
    if (token) {
      axios
        .delete("http://localhost:8000/delete-account", {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then(() => {
          setMessage("Account deleted successfully. Redirecting to signup...");
          localStorage.removeItem("token");
          setTimeout(() => {
            navigate("/signup");
          }, 3000);
        })
        .catch(() => {
          setMessage("Error deleting account.");
        });
    }
  };

  // Logout Function
  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  return (
    <div className={styles.dashboardContainer}>
      <div className={styles.card}>
        <h1 className={styles.heading}>Welcome to Your Dashboard</h1>
        <p className={styles.message}>{message}</p>

        <div className={styles.buttonGroup}>
          <button
            className={`${styles.button} ${styles.deleteButton}`}
            onClick={handleDeleteAccount}
          >
            Delete Account
          </button>
          <button
            className={`${styles.button} ${styles.logoutButton}`}
            onClick={handleLogout}
          >
            Logout
          </button>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
