import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { Button, Card, Spinner, Alert } from 'react-bootstrap';  // Importing Bootstrap components

const Dashboard = () => {
  const [message, setMessage] = useState('');
  const [clubs, setClubs] = useState([]);
  const [events, setEvents] = useState([]);
  const [resources, setResources] = useState([]);
  const [loading, setLoading] = useState(false);  // Loading state for data fetching
  const [activeTab, setActiveTab] = useState('clubs');  // To track the active tab
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      fetchData(token);
    } else {
      setMessage('Access denied. Please log in.');
    }
  }, []);

  const fetchData = (token) => {
    setLoading(true);
    setMessage('');
    
    axios
      .get('http://localhost:8000/dashboard', { headers: { Authorization: `Bearer ${token}` } })
      .then((response) => {
        setMessage(response.data.message);
        
        // Fetch Clubs, Events, Resources concurrently
        Promise.all([
          axios.get('http://localhost:8000/clubs', { headers: { Authorization: `Bearer ${token}` } }),
          axios.get('http://localhost:8000/events', { headers: { Authorization: `Bearer ${token}` } }),
          axios.get('http://localhost:8000/resources', { headers: { Authorization: `Bearer ${token}` } })
        ])
          .then(([clubResponse, eventResponse, resourceResponse]) => {
            setClubs(clubResponse.data.clubs);
            setEvents(eventResponse.data.events);
            setResources(resourceResponse.data.resources);
            setLoading(false);
          })
          .catch((err) => {
            setMessage('Failed to fetch data.');
            setLoading(false);
            console.log(err);
          });
      })
      .catch(() => {
        setMessage('Access denied. Please log in.');
        setLoading(false);
      });
  };

  const handleTabChange = (tab) => {
    setActiveTab(tab);  // Change active tab (clubs, events, or resources)
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const handleDeleteAccount = () => {
    const token = localStorage.getItem('token');
    if (token) {
      axios
        .delete('http://localhost:8000/delete-account', { headers: { Authorization: `Bearer ${token}` } })
        .then(() => {
          setMessage('Account deleted successfully. Redirecting to signup...');
          localStorage.removeItem('token');
          setTimeout(() => {
            navigate('/signup');
          }, 5000);
        })
        .catch(() => {
          setMessage('Error deleting account.');
        });
    }
  };

  return (
    <div className="container mt-4">
      <h1>Dashboard</h1>
      {message && <Alert variant="info">{message}</Alert>}

      {/* Tab Buttons for Clubs, Events, Resources */}
      <div className="mb-3">
        <Button variant="primary" onClick={() => handleTabChange('clubs')} className="mr-2">
          Clubs
        </Button>
        <Button variant="secondary" onClick={() => handleTabChange('events')} className="mr-2">
          Events
        </Button>
        <Button variant="success" onClick={() => handleTabChange('resources')} className="mr-2">
          Resources
        </Button>
      </div>

      {/* Loading Spinner */}
      {loading && <Spinner animation="border" variant="primary" />}

      {/* Conditional Rendering Based on Active Tab */}
      {activeTab === 'clubs' && (
        <div>
          <h2>All Clubs</h2>
          <div className="row">
            {clubs.map((club) => (
              <div className="col-md-4" key={club.club_id}>
                <Card>
                  <Card.Body>
                    <Card.Title>{club.club_name}</Card.Title>
                    <Card.Text>{club.description}</Card.Text>
                  </Card.Body>
                </Card>
              </div>
            ))}
          </div>
        </div>
      )}

      {activeTab === 'events' && (
        <div>
          <h2>Upcoming Events</h2>
          <div className="row">
            {events.map((event) => (
              <div className="col-md-4" key={event.event_id}>
                <Card>
                  <Card.Body>
                    <Card.Title>{event.event_name}</Card.Title>
                    <Card.Text>{event.event_description}</Card.Text>
                    <p>Date: {new Date(event.event_date).toLocaleString()}</p>
                    <p>Location: {event.location}</p>
                  </Card.Body>
                </Card>
              </div>
            ))}
          </div>
        </div>
      )}

      {activeTab === 'resources' && (
        <div>
          <h2>All Resources</h2>
          <div className="row">
            {resources.map((resource) => (
              <div className="col-md-4" key={resource.resource_id}>
                <Card>
                  <Card.Body>
                    <Card.Title>{resource.title}</Card.Title>
                    <Card.Text>Type: {resource.type}</Card.Text>
                    <p>Uploaded on: {new Date(resource.upload_date).toLocaleString()}</p>
                  </Card.Body>
                </Card>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Actions */}
      <div className="mt-4">
        <Button variant="danger" onClick={handleDeleteAccount}>
          Delete Account
        </Button>
        <Button variant="warning" onClick={handleLogout} className="ml-2">
          Logout
        </Button>
      </div>
    </div>
  );
};

export default Dashboard;
