import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { ToastContainer, toast } from "react-toastify";
import axios from "axios";
import "react-toastify/dist/ReactToastify.css";
import styles from "../styles/SignUp.module.css";

const SignUp = () => {
  const [data, setData] = useState({
    user_name: "",
    email: "",
    phone_number: "",
    role: "customer",
    password: "",
    confirmPassword: "",
  });

  const [errors, setErrors] = useState({});
  const [touched, setTouched] = useState({});

  const validate = (values) => {
    const errors = {};
    if (!values.user_name) errors.user_name = "Name is required.";
    if (!values.email) errors.email = "Email is required.";
    if (!values.phone_number) errors.phone_number = "Phone number is required.";
    if (!values.password) errors.password = "Password is required.";
    if (values.password !== values.confirmPassword) errors.confirmPassword = "Passwords do not match.";
    return errors;
  };

  useEffect(() => {
    setErrors(validate(data));
  }, [data]);

  const changeHandler = (event) => {
    setData({ ...data, [event.target.name]: event.target.value });
  };

  const submitHandler = async (event) => {
    event.preventDefault();
    if (Object.keys(errors).length === 0) {
      const url = `http://localhost:5000/signup`;  // Ensure this URL is correct and matches the backend API route
      try {
        const response = await axios.post(url, data);
        if (response.data.success) {
          toast.success("Registered successfully!");
        } else {
          toast.error(response.data.message || "Something went wrong!");
        }
      } catch (error) {
        toast.error("Something went wrong during registration.");
      }
    } else {
      toast.error("Please fix the errors before submitting.");
    }
  };

  return (
    <div className={styles.container}>
      <form onSubmit={submitHandler}>
        <h2>Sign Up</h2>
        <input
          type="text"
          name="user_name"
          placeholder="Name"
          value={data.user_name}
          onChange={changeHandler}
        />
        {errors.user_name && <span>{errors.user_name}</span>}
        <input
          type="email"
          name="email"
          placeholder="Email"
          value={data.email}
          onChange={changeHandler}
        />
        {errors.email && <span>{errors.email}</span>}
        <input
          type="text"
          name="phone_number"
          placeholder="Phone"
          value={data.phone_number}
          onChange={changeHandler}
        />
        {errors.phone_number && <span>{errors.phone_number}</span>}
        <select name="role" value={data.role} onChange={changeHandler}>
          <option value="customer">Customer</option>
          <option value="technician">Technician</option>
        </select>
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={data.password}
          onChange={changeHandler}
        />
        {errors.password && <span>{errors.password}</span>}
        <input
          type="password"
          name="confirmPassword"
          placeholder="Confirm Password"
          value={data.confirmPassword}
          onChange={changeHandler}
        />
        {errors.confirmPassword && <span>{errors.confirmPassword}</span>}
        <button type="submit">Sign Up</button>
        <p>
          Already have an account? <Link to="/login">Sign In</Link>
        </p>
      </form>
      <ToastContainer />
    </div>
  );
};

export default SignUp;
