import React, { useState } from "react";
import axios from "axios";

function CreateEvent() {
  const [eventData, setEventData] = useState({
    event_name: "",
    club_id: "",
    event_date: "",
    event_description: "",
    location: "",
  });
  const [loading, setLoading] = useState(false); // Loading state
  const [error, setError] = useState(""); // Error state

  const handleChange = (e) => {
    setEventData({ ...eventData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError("");

    // Basic validation
    if (
      !eventData.event_name ||
      !eventData.club_id ||
      !eventData.event_date ||
      !eventData.event_description ||
      !eventData.location
    ) {
      setError("All fields are required.");
      setLoading(false);
      return;
    }

    try {
      const response = await axios.post("http://localhost:8000/create-event", eventData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });
      alert(response.data.message); // Show success message
      setEventData({ event_name: "", club_id: "", event_date: "", event_description: "", location: "" }); // Clear form
    } catch (err) {
      console.error("Error Response:", err.response || err);
      setError(err.response?.data?.error || "Failed to create event.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h2>Create Event</h2>
      {error && <p style={{ color: "red" }}>{error}</p>}
      <form onSubmit={handleSubmit}>
        <input
          name="event_name"
          type="text"
          placeholder="Event Name"
          value={eventData.event_name}
          onChange={handleChange}
        />
        <input
          name="club_id"
          type="text"
          placeholder="Club ID"
          value={eventData.club_id}
          onChange={handleChange}
        />
        <input
          name="event_date"
          type="date"
          value={eventData.event_date}
          onChange={handleChange}
        />
        <textarea
          name="event_description"
          placeholder="Event Description"
          value={eventData.event_description}
          onChange={handleChange}
        />
        <input
          name="location"
          type="text"
          placeholder="Location"
          value={eventData.location}
          onChange={handleChange}
        />
        <button type="submit" disabled={loading}>
          {loading ? "Creating..." : "Create Event"}
        </button>
      </form>
    </div>
  );
}

export default CreateEvent;
