import React, { useState, useEffect } from "react";
import axios from "axios";
import { FaDownload, FaShareAlt, FaSearch, FaUpload } from "react-icons/fa";
import "./ResourcesPage.css";

const ResourcesPage = () => {
  const [resources, setResources] = useState([]);
  const [loading, setLoading] = useState(true);
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [searchQuery, setSearchQuery] = useState("");

  // Fetch resources when the component mounts
  useEffect(() => {
    fetchResources();
  }, []);

  const fetchResources = async () => {
    setLoading(true);
    try {
      const response = await axios.get("http://localhost:8000/resources/all");
      setResources(response.data.resources);
      setLoading(false);
    } catch (err) {
      console.error("Error fetching resources:", err);
      setLoading(false);
    }
  };

  // Handle file input changes
  const handleFileChange = (e) => {
    const selectedFile = e.target.files[0];
    console.log("Selected file:", selectedFile); // Debugging line
    setFile(selectedFile);
  };

  // Handle form submission for uploading resources
  const handleUpload = async (e) => {
    e.preventDefault();
    console.log("Upload triggered"); // Debugging line

    if (!file || !title) {
      alert("Please provide a file and title.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);
    formData.append("title", title);
    formData.append("description", description);

    console.log("FormData prepared:", formData); // Debugging line

    try {
      const response = await axios.post(
        "http://localhost:8000/resources/upload",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );
      console.log(response.data); // Debugging line

      if (response.data.success) {
        alert("Resource uploaded successfully!");
        fetchResources();
        setFile(null);
        setTitle("");
        setDescription("");
      } else {
        alert("Failed to upload resource.");
      }
    } catch (error) {
      console.error("Upload error:", error.message);
      alert("An error occurred while uploading.");
    }
  };

  // Filter resources based on search query
  const filteredResources = resources.filter((resource) =>
    resource.title.toLowerCase().includes(searchQuery.toLowerCase())
  );

  return (
    <div className="resources-page">
      <header className="header">
        <h1 className="page-title">Resource Manager</h1>
      </header>

      <div className="main-content">
        {/* Left Panel */}
        <div className="left-panel">
          <div className="search-bar">
            <input
              type="text"
              placeholder="Search resources..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-input"
            />
            <button className="search-btn">
              <FaSearch />
            </button>
          </div>

          <div className="resource-list">
            {loading && <p>Loading resources...</p>}
            {filteredResources.map((resource) => (
              <div key={resource.resource_id} className="resource-card">
                <h3>{resource.title}</h3>
                <p>{resource.description}</p>
                <div className="card-buttons">
                  <button
                    className="btn download-btn"
                    onClick={() => window.open(resource.file_url, "_blank")}
                  >
                    <FaDownload /> Download
                  </button>
                  <button
                    className="btn share-btn"
                    onClick={() =>
                      navigator.clipboard.writeText(resource.file_url)
                    }
                  >
                    <FaShareAlt /> Share
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Right Panel */}
        <div className="right-panel">
          <form onSubmit={handleUpload} className="upload-form">
            <h2>Upload New Resource</h2>
            <input
              type="text"
              placeholder="Resource Title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
              className="input-field"
            />
            <textarea
              placeholder="Description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="textarea-field"
            />
            <input
              type="file"
              onChange={(e) => setFile(e.target.files[0])}
              required
              className="file-input"
            />
            <button type="submit" className="upload-btn">
              Upload
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ResourcesPage;
