export const validate = (data, type) => {
    const errors = {};

    if (!data.name.trim()) errors.name = "Name is required";
    if (!data.email.trim()) errors.email = "Email is required";
    if (!data.phone.trim() || !/^\d{11}$/.test(data.phone)) errors.phone = "Valid phone number is required";

    if (!data.password.trim()) errors.password = "Password is required";
    else if (data.password.length < 6) errors.password = "Password must be at least 6 characters";

    if (data.confirmPassword !== data.password) errors.confirmPassword = "Passwords do not match";

    if (!data.role) errors.role = "Role selection is required";

    if (!data.IsAccepted) errors.IsAccepted = "You must accept the terms";

    return errors;
  };
