import React, { useState, useEffect } from 'react';
import axios from 'axios';

const EventsPage = () => {
  const [events, setEvents] = useState([]);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true); // Added loading state

  useEffect(() => {
    const token = localStorage.getItem('token');
    
    if (token) {
      axios
        .get('http://localhost:8000/events/all', { // Corrected endpoint
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          setEvents(response.data.events);
          setLoading(false); // Set loading to false when data is loaded
        })
        .catch((err) => {
          console.error(err);
          setMessage('Error loading events.');
          setLoading(false); // Set loading to false even in case of error
        });
    } else {
      setMessage('Access denied. Please log in.');
      setLoading(false); // Set loading to false when no token is available
    }
  }, []);

  return (
    <div>
      <h1>All Events</h1>
      {loading && <p>Loading events...</p>} {/* Show loading text */}
      {message && <p>{message}</p>}
      <ul>
        {events.length > 0 ? (
          events.map((event) => (
            <li key={event.event_id}>
              <strong>{event.event_name}</strong>
              <p>{event.event_description}</p>
              <p>Date: {new Date(event.event_date).toLocaleString()}</p>
              <p>Location: {event.location}</p>
            </li>
          ))
        ) : (
          <p>No events available.</p>
        )}
      </ul>
    </div>
  );
};

export default EventsPage;
