import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Pie } from 'react-chartjs-2'; // Importing Pie chart from react-chartjs-2
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();

  // Sample data for alumni demographics
  const alumniData = {
    labels: ['Software Engineers', 'Data Scientists', 'Academics', 'Entrepreneurs', 'Others'],
    datasets: [
      {
        label: 'Alumni Demographics',
        data: [40, 25, 15, 10, 10], // Example data values
        backgroundColor: [
          'rgba(255, 99, 132, 0.6)', // Red
          'rgba(54, 162, 235, 0.6)', // Blue
          'rgba(255, 206, 86, 0.6)', // Yellow
          'rgba(75, 192, 192, 0.6)', // Green
          'rgba(153, 102, 255, 0.6)', // Purple
        ],
        borderColor: [
          'rgba(255, 99, 132, 1)',
          'rgba(54, 162, 235, 1)',
          'rgba(255, 206, 86, 1)',
          'rgba(75, 192, 192, 1)',
          'rgba(153, 102, 255, 1)',
        ],
        borderWidth: 1,
      },
    ],
  };

  // Chart options (optional)
  const chartOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top', // Position of the legend
      },
      title: {
        display: true,
        text: 'Alumni Demographics',
        font: {
          size: 18,
        },
      },
    },
  };

  // Navigation Handlers
  const handleViewClubs = () => {
    navigate('/clubs');
  };

  const handleViewEvents = () => {
    navigate('/events');
  };

  const handleViewResources = () => {
    navigate('/resources');
  };

  const handleDeleteAccount = () => {
    navigate('/delete-account');
  };

  const handleLogout = () => {
    navigate('/logout');
  };

  return (
    <div className="dashboard-container">
      {/* Top-right Buttons */}
      <div className="top-right-buttons">
        <button className="btn btn-danger" onClick={handleDeleteAccount}>
          Delete Account
        </button>
        <button className="btn btn-warning" onClick={handleLogout}>
          Logout
        </button>
      </div>

      {/* Profile Section */}
      <div className="profile-section">
        <div className="profile-info">
          <div className="user-details">
            <h2 className="username">CSECU</h2>
            <p className="user-role">ASSOCIATION</p>
            <button className="btn btn-info">Member</button>
          </div>
        </div>
      </div>

      <h1 className="dashboard-title">
        Welcome to the digital frontier – it’s time to explore
      </h1>

      <div className="button-group">
        <div className="card">
          <button className="btn btn-primary" onClick={handleViewClubs}>
            View All Clubs
          </button>
        </div>
        <div className="card">
          <button className="btn btn-secondary" onClick={handleViewEvents}>
            View All Events
          </button>
        </div>
        <div className="card">
          <button className="btn btn-success" onClick={handleViewResources}>
            View All Resources
          </button>
        </div>
      </div>

      {/* Alumni Demographics Chart */}
      <div className="chart-container">
        <Pie data={alumniData} options={chartOptions} />
      </div>
    </div>
  );
};

export default Dashboard;
