import React, { useState, useEffect } from "react";
import axios from "axios";
import "./ResourcesPage.css";

const ResourcesPage = () => {
  const [resources, setResources] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState("");
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [searchQuery, setSearchQuery] = useState("");
  const [userId] = useState(1);
  const [username] = useState("testUser");

  useEffect(() => {
    fetchResources();
  }, []);

  const fetchResources = async () => {
    setLoading(true);
    try {
      const response = await axios.get("http://localhost:8000/resources/all");
      setResources(response.data.resources);
      setLoading(false);
    } catch (err) {
      console.error("Error fetching resources:", err);
      setMessage("Error loading resources.");
      setLoading(false);
    }
  };

  const handleUpload = async (e) => {
    e.preventDefault();

    if (!file || !title) {
      alert("Please provide a file and title.");
      return;
    }

    const formData = new FormData();
    formData.append("file", file);
    formData.append("title", title);
    formData.append("description", description);
    formData.append("user_id", userId);
    formData.append("username", username);

    try {
      const response = await axios.post(
        "http://localhost:8000/resources/upload",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data",
          },
        }
      );

      if (response.data.success) {
        alert("Resource uploaded successfully!");
        fetchResources(); // Refresh resources
        setFile(null); // Reset form fields
        setTitle("");
        setDescription("");
      } else {
        alert("Failed to upload resource.");
      }
    } catch (error) {
      console.error("Upload error:", error.response || error.message);
      alert(
        error.response?.data?.message ||
          "An error occurred while uploading the resource."
      );
    }
  };

  const handleSearch = () => {
    // Update resource display based on search query
    if (!searchQuery.trim()) {
      alert("Please enter a search query.");
      return;
    }
    setResources((prevResources) =>
      prevResources.filter((resource) =>
        resource.title.toLowerCase().includes(searchQuery.toLowerCase())
      )
    );
  };

  const handleDownload = (fileUrl) => {
    if (!fileUrl) {
      alert("File URL is missing.");
      return;
    }
    window.open(fileUrl, "_blank");
  };

  const handleShare = (fileUrl) => {
    navigator.clipboard
      .writeText(fileUrl)
      .then(() => alert("File URL copied to clipboard!"))
      .catch(() => alert("Failed to copy URL."));
  };

  return (
    <div className="resources-page">
      <header className="header">
        <button className="back-btn" onClick={() => window.history.back()}>
          ← Go Back
        </button>
        <h1 className="page-title">Resource Manager</h1>
      </header>

      <div className="main-content">
        {/* Left Panel */}
        <div className="left-panel">
          <div className="search-container">
            <input
              type="text"
              placeholder="Search resources..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="search-input"
            />
            <button onClick={handleSearch} className="search-btn">
              Search
            </button>
          </div>

          <div className="resource-list">
            {loading && <p>Loading...</p>}
            {message && <p>{message}</p>}
            {resources.map((resource) => (
              <div key={resource.resource_id} className="resource-card">
                <h3>{resource.title}</h3>
                <p>{resource.description}</p>
                <div className="card-buttons">
                  <button
                    className="download-btn"
                    onClick={() => handleDownload(resource.file_url)}
                  >
                    Download
                  </button>
                  <button
                    className="share-btn"
                    onClick={() => handleShare(resource.file_url)}
                  >
                    Share
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Right Panel */}
        <div className="right-panel">
          <form onSubmit={handleUpload} className="upload-form">
            <h2>Upload New Resource</h2>
            <input
              type="text"
              placeholder="Resource Title"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
              className="input-field"
            />
            <textarea
              placeholder="Description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              className="textarea-field"
            />
            <input
              type="file"
              onChange={(e) => setFile(e.target.files[0])}
              required
              className="file-input"
            />
            <button type="submit" className="upload-btn">
              Upload
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default ResourcesPage;
