import React, { useEffect } from 'react';
import { Chart } from 'chart.js/auto';
import './FrontPage.css';

function FrontPage() {
  useEffect(() => {
    // Ensure the chart is only rendered once
    const canvas = document.getElementById('demographicsChart');
    if (canvas) {
      const ctx = canvas.getContext('2d');

      new Chart(ctx, {
        type: 'bar',
        data: {
          labels: ['Undergraduates', 'Postgraduates', 'PhD', 'Faculty'], // X-axis labels
          datasets: [
            {
              label: 'Population',
              data: [300, 100, 50, 22], // Example data
              backgroundColor: [
                'rgba(75, 192, 192, 0.5)',
                'rgba(54, 162, 235, 0.5)',
                'rgba(255, 206, 86, 0.5)',
                'rgba(153, 102, 255, 0.5)',
              ],
              borderColor: [
                'rgba(75, 192, 192, 1)',
                'rgba(54, 162, 235, 1)',
                'rgba(255, 206, 86, 1)',
                'rgba(153, 102, 255, 1)',
              ],
              borderWidth: 1,
            },
          ],
        },
        options: {
          responsive: true,
          plugins: {
            legend: {
              position: 'top',
            },
            title: {
              display: true,
              text: 'CSECU Demographics',
            },
          },
          scales: {
            y: {
              beginAtZero: true, // Y-axis starts from 0
            },
          },
        },
      });
    }
  }, []);

  return (
    <div className="front-page">
      {/* Logo Section */}
      <header className="header">
        <img src="/logo.png" alt="CSECU Logo" className="logo" />
        <h1 className="hero-title">Welcome to CSECU!</h1>
        <p className="hero-subtitle">Join us in revolutionizing Computer Science and Engineering</p>
      </header>

      {/* About Section */}
      <section className="about-cse">
        <p>
          The Department of Computer Science & Engineering (CSE) at the University of Chittagong
          launched its undergraduate major programme in 2001 and its postgraduate programme in 2010.
          CSE department currently hosts 22 faculty members, including eight Professors, and around
          400 students.
        </p>
      </section>

      {/* Chart Section */}
      <section className="chart-section">
        <h2>Demographics Overview</h2>
        <canvas id="demographicsChart"></canvas>
      </section>

      {/* Buttons Section */}
      <div className="button-group">
        <button className="btn primary" onClick={() => console.log('Navigate to signup')}>
          Get Started
        </button>
        <button className="btn secondary" onClick={() => console.log('Navigate to quotes')}>
          Get Quotes
        </button>
      </div>
    </div>
  );
}

export default FrontPage;
