import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './EventDetails.css';

const EventDetails = () => {
  const { eventId } = useParams();
  const navigate = useNavigate();
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');

  useEffect(() => {
    axios
      .get(`http://localhost:8000/events/${eventId}`)
      .then((response) => {
        if (response.data.event) {
          setEvent(response.data.event);
        } else {
          setMessage('Event not found.');
        }
        setLoading(false);
      })
      .catch((error) => {
        console.error('Error fetching event details:', error);
        setMessage('Failed to load event details.');
        setLoading(false);
      });
  }, [eventId]);

  const handleRegister = () => {
    alert(`Successfully registered for ${event.event_name}`);
  };

  if (loading) return <p>Loading event details...</p>;
  if (message) return <p className="error-message">{message}</p>;

  return (
    <div className="event-details-page">
      <button onClick={() => navigate('/events')} className="back-btn">Back to Events</button>
      {event && (
        <div className="event-details-container">
          <h1>{event.event_name}</h1>
          <p>{event.description}</p>
          <p><strong>Date:</strong> {event.date}</p>
          <p><strong>Location:</strong> {event.location}</p>
          <div className="media">
            {event.photos.map((photo, index) => (
              <img key={index} src={photo} alt={`Event Photo ${index + 1}`} />
            ))}
            {event.videos.map((video, index) => (
              <video key={index} controls>
                <source src={video} type="video/mp4" />
                Your browser does not support the video tag.
              </video>
            ))}
          </div>
          <button onClick={handleRegister} className="register-btn">Register</button>
        </div>
      )}
    </div>
  );
};

export default EventDetails;
