import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [userData, setUserData] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // Get the JWT token from localStorage
  const token = localStorage.getItem('token');

  // If no token, redirect to login page
  useEffect(() => {
    if (!token) {
      navigate('/login');
      return;
    }

    const fetchUserData = async () => {
      try {
        // Call the protected dashboard API with the JWT token
        const response = await fetch('http://localhost:8000/dashboard', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });

        const data = await response.json();

        if (response.ok) {
          setUserData(data);  // Set the fetched user data (success message)
        } else {
          setError(data.message);  // Set error message if response is not okay
        }

      } catch (err) {
        setError('Error fetching user data.');
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();  // Fetch the user data
  }, [token, navigate]);  // Dependency on token and navigate

  // Loading state
  if (loading) {
    return <div>Loading...</div>;
  }

  // Error state
  if (error) {
    return <div>{error}</div>;
  }

  // Success state - Render dashboard data
  return (
    <div>
      <h1>Dashboard</h1>
      {userData?.success && userData.message && (
        <div>
          <h2>{userData.message}</h2>
          {/* Display different content based on role */}
          {userData.message.includes('Admin') ? (
            <div>
              <h3>Admin Section</h3>
              <p>Welcome, Admin! You have full access to the platform.</p>
              {/* Add more Admin-specific content here */}
            </div>
          ) : (
            <div>
              <h3>Member Section</h3>
              <p>Welcome, Member! You have limited access.</p>
              {/* Add more Member-specific content here */}
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default Dashboard;
