import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [message, setMessage] = useState('');
  const navigate = useNavigate(); // To navigate after deletion

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.get('http://localhost:8000/dashboard', {
        headers: { Authorization: `Bearer ${token}` }
      })
      .then(response => {
        setMessage(response.data.message);
      })
      .catch(error => {
        setMessage('Access denied. Please log in.');
      });
    } else {
      setMessage('Access denied. Please log in.');
    }
  }, []);

  // Delete Account Function
  const handleDeleteAccount = () => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.delete('http://localhost:8000/delete-account', {
        headers: { Authorization: `Bearer ${token}` }
      })
      .then(response => {
        setMessage('Your account is being deleted. Redirecting to signup...');
        localStorage.removeItem('token'); // Remove token on successful deletion

        // Redirect after 5 seconds
        setTimeout(() => {
          navigate('/signup'); // Redirect to signup page
        }, 5000);
      })
      .catch(error => {
        setMessage('Error deleting account.');
      });
    }
  };

  // Logout Function
  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login'); // Redirect to login page after logout
  };

  return (
    <div style={{ position: 'relative' }}>
      {/* Buttons at the top-left corner */}
      <button
        onClick={handleDeleteAccount}
        style={{
          position: 'absolute',
          top: '10px',
          left: '10px',
          padding: '10px',
          backgroundColor: 'red',
          color: 'white',
        }}
      >
        Delete Account
      </button>
      <button
        onClick={handleLogout}
        style={{
          position: 'absolute',
          top: '50px',
          left: '10px',
          padding: '10px',
          backgroundColor: 'gray',
          color: 'white',
        }}
      >
        Logout
      </button>

      <h1>Dashboard</h1>
      <p>{message}</p>
    </div>
  );
};

export default Dashboard;
