import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './componentEventDetails.css';

const EventDetails = () => {
  const { eventId } = useParams();
  const navigate = useNavigate();
  const [event, setEvent] = useState(null);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');

  useEffect(() => {
    axios
      .get(`http://localhost:8000/events/${eventId}`)
      .then((response) => {
        if (response.data && response.data.event) {
          setEvent(response.data.event);
        } else {
          setMessage('Event not found.');
        }
        setLoading(false);
      })
      .catch((err) => {
        console.error('Error fetching event details:', err);
        setMessage('Error loading event details. Please try again later.');
        setLoading(false);
      });
  }, [eventId]);

  const handleRegister = () => {
    alert(`Successfully registered for event: ${event?.event_name}`);
  };

  if (loading) {
    return <div className="loading-container">Loading event details...</div>;
  }

  if (message) {
    return <p className="error-message">{message}</p>;
  }

  return (
    <div className="event-details-page">
      <button className="back-btn" onClick={() => navigate('/events')}>
        Back to Events
      </button>
      {event && (
        <div className="event-details-container">
          <h1 className="event-title">{event.event_name}</h1>
          <p className="event-description">{event.description}</p>
          <p className="event-date"><strong>Date:</strong> {event.date}</p>
          <p className="event-location"><strong>Location:</strong> {event.location}</p>
          
          <div className="media-section">
            <h3>Event Photos & Videos</h3>
            <div className="media-container">
              {event.photos?.map((photo, index) => (
                <img key={index} src={photo} alt={`Event Photo ${index + 1}`} className="event-photo" />
              ))}
              {event.videos?.map((video, index) => (
                <video key={index} controls className="event-video">
                  <source src={video} type="video/mp4" />
                  Your browser does not support the video tag.
                </video>
              ))}
            </div>
          </div>

          <button className="register-btn" onClick={handleRegister}>
            Register for Event
          </button>
        </div>
      )}
    </div>
  );
};

export default EventDetails;
