import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './ClubDetailsPage.css';

const ClubDetailsPage = () => {
  const { clubId } = useParams();
  const [club, setClub] = useState(null);

  useEffect(() => {
    // Fetch club details from the server
    axios.get(`http://localhost:8000/clubs/${clubId}`)
      .then(response => {
        const clubData = response.data.club;
        setClub({
          name: clubData.club_name,
          description: clubData.description,
          startDate: new Date(clubData.creation_date).toLocaleDateString(),
          totalMembers: clubData.total_members,
          about: clubData.about || 'No additional information available',
          photo: clubData.photo || '',  // Handle main photo URL
          additionalPhotos: clubData.additional_photos || [],  // Handle additional photos
          video: clubData.video || ''  // Handle club video URL
        });
      })
      .catch(error => {
        console.error('Error fetching club details:', error);
      });
  }, [clubId]);

  return (
    <div className="club-details-page">
      {club ? (
        <div className="club-details-container">
          <h2 className="club-name">{club.name}</h2>
          
          {club.photo && (
            <img src={`http://localhost:8000/${club.photo}`} alt={club.name} className="club-photo" />
          )}

          {club.video && (
            <div className="club-video">
              <video controls>
                <source src={`http://localhost:8000/${club.video}`} type="video/mp4" />
                Your browser does not support the video tag.
              </video>
            </div>
          )}

          <p className="club-description">{club.description}</p>
          <p className="club-start-date"><strong>Started on:</strong> {club.startDate}</p>
          <p className="club-members"><strong>Total Members:</strong> {club.totalMembers}</p>
          <p className="club-about"><strong>About the Club:</strong> {club.about}</p>

          {club.additionalPhotos.length > 0 && (
            <div className="club-gallery">
              <h3>Club Gallery</h3>
              <div className="gallery-container">
                {club.additionalPhotos.map((photo, index) => (
                  <img key={index} src={`http://localhost:8000/${photo}`} alt={`club photo ${index + 1}`} className="gallery-image" />
                ))}
              </div>
            </div>
          )}
        </div>
      ) : (
        <p>Loading club details...</p>
      )}
    </div>
  );
};

export default ClubDetailsPage;
