import React, { useState } from 'react';
import axios from 'axios';

const SearchTechnician = () => {
  const [location, setLocation] = useState('');
  const [technicians, setTechnicians] = useState([]);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  // Handle search button click
  const handleSearch = async () => {
    if (!location) {
      setError('Please select a location.');
      return;
    }
    setError('');
    setTechnicians([]);
    setLoading(true);

    try {
      // Sending GET request to backend API
      const response = await axios.get('http://localhost:8000/search-technicians', {
        params: { location },
      });

      // Log the response for debugging
      console.log(response.data);

      if (response.data.success) {
        setTechnicians(response.data.technicians);
      } else {
        setError('No technicians found for the given location.');
      }
    } catch (err) {
      console.error(err);
      setError('An error occurred while searching for technicians.');
    } finally {
      setLoading(false);
    }
  };

  // Handle location selection change
  const handleLocationChange = (e) => {
    setLocation(e.target.value);
    setTechnicians([]); // Clear technicians when location changes
    setError(''); // Clear error message
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Search Technicians by Location</h2>

      <div style={{ marginBottom: '10px' }}>
        <label htmlFor="location" style={{ marginRight: '10px' }}>Location:</label>
        <select
          id="location"
          value={location}
          onChange={handleLocationChange}
        >
          <option value="">--Select Location--</option>
          <option value="New York">New York</option>
          <option value="Los Angeles">Los Angeles</option>
          <option value="Chicago">Chicago</option>
          <option value="San Francisco">San Francisco</option>
        </select>
      </div>

      <button onClick={handleSearch} style={{ padding: '10px 20px', marginBottom: '20px' }}>
        Search
      </button>

      {loading && <p>Searching...</p>} {/* Loading state */}

      {error && <p style={{ color: 'red' }}>{error}</p>} {/* Error state */}

      {/* Render list of technicians if available */}
      {technicians.length > 0 ? (
        <div>
          <h3>Available Technicians:</h3>
          <ul>
            {technicians.map((tech) => (
              <li key={tech.user_id}>
                <strong>{tech.user_name}</strong> ({tech.email}, {tech.phone_number})
              </li>
            ))}
          </ul>
        </div>
      ) : (
        <p>No technicians found or please select a location.</p>
      )}
    </div>
  );
};

export default SearchTechnician;
