import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./AdminDashboard.css";

function AdminDashboard() {
  const navigate = useNavigate();
  const [user, setUser] = useState({});
  const [coverImages, setCoverImages] = useState({
    clubs: "",
    events: "",
    resources: "",
    shareThoughts: "",
    viewUsers: "",
    createClub: "",
    createEvent: "",
  });

  // Simulate getting logged-in user info
  useEffect(() => {
    const loggedInUser = JSON.parse(localStorage.getItem("user"));
    setUser(loggedInUser || { username: "Admin", role: "Administrator" });
  }, []);

  const handleViewClubs = () => navigate("/clubs");
  const handleViewEvents = () => navigate("/events");
  const handleViewResources = () => navigate("/resources");
  const handleShareThoughts = () => navigate("/share-thought");
  const handleViewUsers = () => navigate("/all-users");
  const handleCreateClub = () => navigate("/create-club");
  const handleCreateEvent = () => navigate("/create-event");

  const handleLogout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  const handleCoverImageChange = (e, card) => {
    const file = e.target.files[0];
    if (file) {
      const imageUrl = URL.createObjectURL(file);
      setCoverImages((prev) => ({
        ...prev,
        [card]: imageUrl,
      }));
    }
  };

  return (
    <div className="dashboard">
      <div className="topbar">
        <div className="logo">Admin Dashboard</div>
        <div className="profile">
          <p>{user.username}</p>
          <p className="role">{user.role}</p>
        </div>
        <div className="actions">
          <button onClick={handleLogout} className="logout-btn">
            Logout
          </button>
        </div>
      </div>

      <div className="content">
        <h1>Welcome, {user.username}</h1>
        <p>Explore the admin functionalities below:</p>

        {/* Cards Section */}
        <div className="dashboard-cards">
          {["clubs", "events", "resources", "shareThoughts", "viewUsers", "createClub", "createEvent"].map((card) => (
            <div key={card} className="card" onClick={() => navigate(`/${card}`)} style={{ backgroundImage: `url(${coverImages[card]})` }}>
              <div className="card-title">{card.replace(/([A-Z])/g, ' $1').toUpperCase()}</div>

              {/* File input to change cover image for this specific card */}
              <input
                type="file"
                accept="image/*"
                onChange={(e) => handleCoverImageChange(e, card)}
                className="file-input"
              />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default AdminDashboard;
