import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const [message, setMessage] = useState('');
  const [role, setRole] = useState('');
  const [experiencedYear, setExperiencedYear] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios
        .get('http://localhost:8000/dashboard', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          setMessage(response.data.message);
          setRole(response.data.role); // Assuming the role is sent from the backend
        })
        .catch(() => {
          setMessage('Access denied. Please log in.');
        });
    } else {
      setMessage('Access denied. Please log in.');
    }
  }, []);

  const handleUpdateExperience = () => {
    const token = localStorage.getItem('token');
    if (token && experiencedYear) {
      axios
        .put(
          'http://localhost:8000/update-experience',
          { experienced_year: parseInt(experiencedYear) },
          { headers: { Authorization: `Bearer ${token}` } }
        )
        .then((response) => {
          setSuccessMessage(response.data.message);
          setErrorMessage('');
        })
        .catch((error) => {
          setErrorMessage(error.response?.data?.message || 'Error updating experience.');
          setSuccessMessage('');
        });
    }
  };

  const handleDeleteAccount = () => {
    const token = localStorage.getItem('token');
    if (token) {
      axios
        .delete('http://localhost:8000/delete-account', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then(() => {
          setMessage('Account deleted successfully. Redirecting to signup...');
          localStorage.removeItem('token');
          setTimeout(() => {
            navigate('/signup');
          }, 5000);
        })
        .catch(() => {
          setMessage('Error deleting account.');
        });
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <div>
      <h1>Dashboard</h1>
      <p>{message}</p>

      {role === 'technician' && (
        <div>
          <h3>Update Experience</h3>
          <input
            type="number"
            placeholder="Enter your years of experience"
            value={experiencedYear}
            onChange={(e) => setExperiencedYear(e.target.value)}
          />
          <button onClick={handleUpdateExperience}>Update Experience</button>
          {successMessage && <p style={{ color: 'green' }}>{successMessage}</p>}
          {errorMessage && <p style={{ color: 'red' }}>{errorMessage}</p>}
        </div>
      )}

      <button onClick={handleDeleteAccount}>Delete Account</button>
      <button onClick={handleLogout}>Logout</button>
    </div>
  );
};

export default Dashboard;
