import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ResourcesPage = () => {
  const [resources, setResources] = useState([]);
  const [message, setMessage] = useState('');
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const user_id = localStorage.getItem('user_id'); // Store user_id from localStorage

  useEffect(() => {
    const fetchResources = async () => {
      try {
        const response = await axios.get('http://localhost:8000/resources/all');
        setResources(response.data.resources);
      } catch (error) {
        console.error('Error fetching resources:', error.message);
        setMessage('Error fetching resources.');
      }
    };

    fetchResources();
  }, []);

  const handleUpload = async (e) => {
    e.preventDefault();

    if (!file || !title || !user_id) {
      alert('Please fill all fields.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);
    formData.append('title', title);
    formData.append('description', description);
    formData.append('user_id', user_id);

    try {
      const response = await axios.post('http://localhost:8000/resources/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      if (response.data.success) {
        setResources([...resources, response.data.resource]);
        alert('File uploaded successfully!');
      } else {
        alert('File upload failed.');
      }
    } catch (error) {
      console.error('Error uploading file:', error.message);
      alert('Error uploading file.');
    }
  };

  return (
    <div>
      <h1>Resources</h1>

      <form onSubmit={handleUpload}>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        <textarea
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <input type="file" onChange={(e) => setFile(e.target.files[0])} required />
        <button type="submit">Upload</button>
      </form>

      {resources.map((resource) => (
        <div key={resource.resource_id}>
          <h3>{resource.title}</h3>
          <p>{resource.description}</p>
          <a href={resource.file_url} target="_blank" rel="noopener noreferrer">
            Download
          </a>
        </div>
      ))}
    </div>
  );
};

export default ResourcesPage;
