import React, { useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const navigate = useNavigate();

  const topRightContainerRef = useRef(null);

  useEffect(() => {
    // Dynamically position the top-right container
    const container = topRightContainerRef.current;
    if (container) {
      container.style.position = 'absolute';
      container.style.top = '20px';
      container.style.right = '20px';
      container.style.display = 'flex';
      container.style.gap = '10px';
    }
  }, []);

  const handleViewClubs = () => {
    navigate('/clubs');
  };

  const handleViewEvents = () => {
    navigate('/events');
  };

  const handleViewResources = () => {
    navigate('/resources');
  };

  const handleDeleteAccount = () => {
    navigate('/delete-account');
  };

  const handleLogout = () => {
    navigate('/logout');
  };

  return (
    <div className="dashboard-container" style={{ position: 'relative', padding: '20px' }}>
      {/* Top-right actions */}
      <div ref={topRightContainerRef}>
        <button
          onClick={handleDeleteAccount}
          style={{
            padding: '10px 15px',
            backgroundColor: '#e3342f',
            color: '#fff',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
          }}
        >
          Delete Account
        </button>
        <button
          onClick={handleLogout}
          style={{
            padding: '10px 15px',
            backgroundColor: '#6c757d',
            color: '#fff',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
          }}
        >
          Logout
        </button>
      </div>

      {/* Main Content */}
      <h1 style={{ textAlign: 'center', margin: '40px 0', color: '#333' }}>Dashboard</h1>
      <div style={{ display: 'flex', justifyContent: 'center', gap: '15px' }}>
        <button
          onClick={handleViewClubs}
          style={{
            padding: '12px 20px',
            backgroundColor: '#007bff',
            color: '#fff',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
          }}
        >
          View All Clubs
        </button>
        <button
          onClick={handleViewEvents}
          style={{
            padding: '12px 20px',
            backgroundColor: '#007bff',
            color: '#fff',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
          }}
        >
          View All Events
        </button>
        <button
          onClick={handleViewResources}
          style={{
            padding: '12px 20px',
            backgroundColor: '#007bff',
            color: '#fff',
            border: 'none',
            borderRadius: '5px',
            cursor: 'pointer',
          }}
        >
          View All Resources
        </button>
      </div>
    </div>
  );
};

export default Dashboard;
