import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaUserCircle, FaSignOutAlt, FaTrashAlt, FaChevronDown } from 'react-icons/fa';

const Dashboard = () => {
  const navigate = useNavigate();

  const [user, setUser] = useState(null);
  const [showMenu, setShowMenu] = useState(false);
  const [loading, setLoading] = useState(true); // Loading state

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        // Simulate a network request delay
        const simulatedUserData = {
          name: 'John Doe',
          email: 'john.doe@example.com',
          profilePic: 'https://via.placeholder.com/100', // Placeholder profile picture
        };
        
        // Simulate a delay to mimic a network request
        setTimeout(() => {
          setUser(simulatedUserData); // Set the fetched data to the state
          setLoading(false); // Once the data is fetched, set loading to false
        }, 1000);
      } catch (error) {
        console.error('Error fetching user data:', error);
        setLoading(false);
      }
    };

    fetchUserData();
  }, []); // Empty dependency array, fetch data on mount

  const handleNavigate = (path) => navigate(path);

  // If the user is still loading, display a loading message
  if (loading) {
    return (
      <div style={{ textAlign: 'center', marginTop: '20px' }}>
        <h2>Loading user data...</h2>
      </div>
    );
  }

  // If user data is not available, display an error
  if (!user) {
    return (
      <div style={{ textAlign: 'center', marginTop: '20px' }}>
        <h2>Error: User data could not be fetched</h2>
      </div>
    );
  }

  return (
    <div
      style={{
        fontFamily: "'Roboto', sans-serif",
        backgroundColor: '#f9fafb',
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
      }}
    >
      {/* Header */}
      <header
        style={{
          backgroundColor: '#1f2937',
          color: '#fff',
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '20px 40px',
          position: 'sticky',
          top: 0,
          zIndex: 10,
          boxShadow: '0 4px 10px rgba(0, 0, 0, 0.1)',
        }}
      >
        {/* Logo */}
        <h1 style={{ fontSize: '1.8rem', fontWeight: 'bold' }}>CSE CU Association</h1>

        {/* User Actions */}
        <div style={{ position: 'relative' }}>
          {user && !loading ? (
            <div
              style={{
                display: 'flex',
                alignItems: 'center',
                gap: '15px',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
              }}
              onClick={() => setShowMenu(!showMenu)}
            >
              <img
                src={user.profilePic}
                alt="Profile"
                style={{
                  width: '45px',
                  height: '45px',
                  borderRadius: '50%',
                  boxShadow: '0 0 10px rgba(0, 0, 0, 0.2)',
                }}
              />
              <div>
                <p style={{ margin: 0, fontWeight: 'bold' }}>{user.name}</p>
                <small style={{ color: '#9ca3af' }}>{user.email}</small>
              </div>
              <FaChevronDown
                size={18}
                style={{
                  color: '#9ca3af',
                  transition: 'transform 0.3s',
                  transform: showMenu ? 'rotate(180deg)' : 'rotate(0deg)',
                }}
              />
            </div>
          ) : (
            <div style={{ color: '#fff' }}>Loading...</div>
          )}

          {/* Dropdown Menu */}
          {showMenu && (
            <div
              style={{
                position: 'absolute',
                top: '70px',
                right: 0,
                backgroundColor: '#fff',
                boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
                borderRadius: '8px',
                overflow: 'hidden',
                width: '200px',
                zIndex: 100,
              }}
            >
              <div style={menuItemStyle} onClick={() => handleNavigate('/profile')}>
                <FaUserCircle size={18} />
                Profile
              </div>
              <div style={menuItemStyle} onClick={() => handleNavigate('/about')}>
                <FaUserCircle size={18} />
                About
              </div>
              <div style={menuItemStyle} onClick={() => handleNavigate('/logout')}>
                <FaSignOutAlt size={18} />
                Logout
              </div>
              <div style={menuItemStyle} onClick={() => handleNavigate('/delete-account')}>
                <FaTrashAlt size={18} />
                Delete Account
              </div>
            </div>
          )}
        </div>
      </header>

      {/* Welcome Section */}
      {user && !loading && (
        <div
          style={{
            padding: '25px',
            backgroundColor: '#2563eb',
            color: '#fff',
            textAlign: 'center',
            borderRadius: '8px',
            margin: '20px auto',
            maxWidth: '800px',
            boxShadow: '0 4px 6px rgba(0, 0, 0, 0.2)',
          }}
        >
          <h2>Welcome back, {user.name}!</h2>
          <p>Here are your available options:</p>
        </div>
      )}

      {/* Main Content */}
      <main
        style={{
          flexGrow: 1,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          gap: '30px',
          padding: '0 20px',
        }}
      >
        <div
          style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
            gap: '20px',
            width: '100%',
            maxWidth: '900px',
          }}
        >
          <button
            onClick={() => handleNavigate('/clubs')}
            style={buttonStyle('#3b82f6')}
          >
            View Clubs
          </button>
          <button
            onClick={() => handleNavigate('/events')}
            style={buttonStyle('#10b981')}
          >
            View Events
          </button>
          <button
            onClick={() => handleNavigate('/resources')}
            style={buttonStyle('#f59e0b')}
          >
            View Resources
          </button>
        </div>
      </main>

      {/* Footer */}
      <footer
        style={{
          textAlign: 'center',
          padding: '20px',
          backgroundColor: '#1f2937',
          color: '#9ca3af',
          marginTop: '40px',
        }}
      >
        © {new Date().getFullYear()} CSE CU Association. All rights reserved.
      </footer>
    </div>
  );
};

// Reusable styles
const menuItemStyle = {
  padding: '12px 20px',
  display: 'flex',
  alignItems: 'center',
  gap: '12px',
  color: '#1f2937',
  cursor: 'pointer',
  borderBottom: '1px solid #e5e7eb',
  fontSize: '14px',
  transition: 'background-color 0.3s',
};

const buttonStyle = (bgColor) => ({
  padding: '20px',
  backgroundColor: bgColor,
  color: '#fff',
  border: 'none',
  borderRadius: '10px',
  cursor: 'pointer',
  fontSize: '16px',
  fontWeight: '600',
  boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
  transition: 'transform 0.2s, box-shadow 0.2s',
  textAlign: 'center',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
  ':hover': {
    transform: 'scale(1.05)',
    boxShadow: '0 6px 10px rgba(0, 0, 0, 0.15)',
  },
});

export default Dashboard;
