import React, { useState, useEffect } from "react";
import axios from 'axios';
import './CreateEvent.css';

function CreateEvent() {
  const [events, setEvents] = useState([]);  // To store the list of available events

  useEffect(() => {
    // Fetch events along with registered users when the component mounts
    const fetchEvents = async () => {
      try {
        const response = await axios.get("http://localhost:8000/get-events-with-users", {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        });
        setEvents(response.data.events);  // Assuming the response contains an "events" field
      } catch (error) {
        alert("Failed to load events");
      }
    };

    fetchEvents();
  }, []);

  return (
    <div className="create-event-container">
      {/* Left side: List of Events */}
      <div className="event-list">
        <h3>Available Events</h3>
        <ul>
          {events.map((event) => (
            <li key={event.id} className="event-item">
              <h4>{event.event_name}</h4>
              <p><strong>Date:</strong> {event.event_date}</p>
              <p><strong>Location:</strong> {event.location}</p>
              <p><strong>Description:</strong> {event.event_description}</p>

              {/* Display Users Registered for this Event */}
              <div className="user-list">
                <h5>Registered Users:</h5>
                <ul>
                  {event.users.map((user) => (
                    <li key={user.id}>
                      <p><strong>{user.name}</strong> (ID: {user.id})</p>
                    </li>
                  ))}
                </ul>
              </div>
            </li>
          ))}
        </ul>
      </div>

      {/* Right side: Create Event Form */}
      <div className="create-event-form">
        <h2>Create Event</h2>
        {/* Form components for creating an event */}
        {/* (Similar to the earlier code) */}
      </div>
    </div>
  );
}

export default CreateEvent;
