import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./AdminDashboard.css";

function AdminDashboard() {
  const navigate = useNavigate();
  const [user, setUser] = useState({}); // To store user info (username, role)
  const [caption, setCaption] = useState("Engineering innovation, shaping tomorrow");

  // Fetch user data from localStorage when the component mounts
  useEffect(() => {
    const loggedInUser = JSON.parse(localStorage.getItem("user"));
    console.log("Logged in user:", loggedInUser);  // Check what data is being retrieved
    if (loggedInUser) {
      setUser(loggedInUser);  // Set user data from localStorage
    } else {
      navigate("/login");  // Redirect to login if no user data is found
    }
  }, [navigate]);

  // Dynamically update caption based on user role
  useEffect(() => {
    console.log("User updated:", user); // Log user data
    if (user.role === 'Admin') {
      setCaption("Welcome, Admin!");
    } else if (user.role === 'User') {
      setCaption("Welcome to your dashboard");
    }
  }, [user]);  // Run whenever user data changes

  // Navigation handlers
  const handleViewClubs = () => {
    console.log("Navigating to view clubs");
    navigate('/clubs');
  };

  const handleViewEvents = () => {
    console.log("Navigating to view events");
    navigate('/events');
  };

  const handleViewResources = () => {
    console.log("Navigating to view resources");
    navigate('/resources');
  };

  const handleCreateClub = () => {
    console.log("Navigating to create club");
    navigate("/create-club");
  };

  const handleCreateEvent = () => {
    console.log("Navigating to create event");
    navigate("/create-event");
  };

  const handleLogout = () => {
    console.log("Logging out...");
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  const handleDeleteAccount = () => {
    console.log("Deleting account...");
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    navigate("/login");
  };

  return (
    <div className="dashboard">
      <div className="profile-section">
        <div className="profile-info">
          <p className="username">{user.username}</p>
          <p className="role">{user.role}</p>
        </div>
        
        <div className="profile-actions">
          <button onClick={handleDeleteAccount} className="delete-btn">
            Delete Account
          </button>
          <button onClick={handleLogout} className="logout-btn">
            Logout
          </button>
        </div>
      </div>

      <div className="dashboard-content">
        <h1>{caption}</h1>

        <div className="admin-actions">
          <button onClick={handleViewClubs} className="action-btn">View All Clubs</button>
          <button onClick={handleViewEvents} className="action-btn">View All Events</button>
          <button onClick={handleViewResources} className="action-btn">View All Resources</button>
        </div>

        <div className="create-actions">
          <button onClick={handleCreateClub} className="action-btn">Create Club</button>
          <button onClick={handleCreateEvent} className="action-btn">Create Event</button>
        </div>
      </div>
    </div>
  );
}

export default AdminDashboard;
