import React, { useState } from "react";
import axios from 'axios';
import './Createevent.css'; 

function CreateEvent() {
  const [eventData, setEventData] = useState({
    event_name: "",
    club_id: "",
    event_date: "",
    event_description: "",
    location: "",
    cover_photo: null, // for the event cover photo
    video: null,       // for the event video
  });

  const handleChange = (e) => {
    const { name, value, files } = e.target;

    if (name === "cover_photo" || name === "video") {
      // If the input is a file, save the file in state
      setEventData({ ...eventData, [name]: files[0] });
    } else {
      setEventData({ ...eventData, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const formData = new FormData();
    formData.append("event_name", eventData.event_name);
    formData.append("club_id", eventData.club_id);
    formData.append("event_date", eventData.event_date);
    formData.append("event_description", eventData.event_description);
    formData.append("location", eventData.location);

    // Append the files if they exist
    if (eventData.cover_photo) formData.append("cover_photo", eventData.cover_photo);
    if (eventData.video) formData.append("video", eventData.video);

    try {
      const response = await axios.post("http://localhost:8000/create-event", formData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
          'Content-Type': 'multipart/form-data',  // To handle file uploads
        },
      });
      alert(response.data.message);  // Show success message
    } catch (error) {
      alert(error.response?.data?.error || "Failed to create event");
    }
  };

  return (
    <div>
      <h2>Create Event</h2>
      <form onSubmit={handleSubmit}>
        <input
          name="event_name"
          type="text"
          placeholder="Event Name"
          value={eventData.event_name}
          onChange={handleChange}
        />
        <input
          name="club_id"
          type="text"
          placeholder="Club ID"
          value={eventData.club_id}
          onChange={handleChange}
        />
        <input
          name="event_date"
          type="date"
          value={eventData.event_date}
          onChange={handleChange}
        />
        <textarea
          name="event_description"
          placeholder="Event Description"
          value={eventData.event_description}
          onChange={handleChange}
        />
        <input
          name="location"
          type="text"
          placeholder="Location"
          value={eventData.location}
          onChange={handleChange}
        />
        {/* File inputs for cover photo and video */}
        <div>
          <label>Event Cover Photo</label>
          <input
            name="cover_photo"
            type="file"
            accept="image/*"
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Event Video</label>
          <input
            name="video"
            type="file"
            accept="video/*"
            onChange={handleChange}
          />
        </div>
        <button type="submit">Create Event</button>
      </form>
    </div>
  );
}

export default CreateEvent;
