import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Dashboard.css';

const Dashboard = () => {
  const navigate = useNavigate();

  // State to store user details
  const [userDetails, setUserDetails] = useState(null);

  // Fetch user details from localStorage
  useEffect(() => {
    const storedUserDetails = localStorage.getItem('userDetails');
    if (storedUserDetails) {
      try {
        const parsedDetails = JSON.parse(storedUserDetails);
        setUserDetails(parsedDetails);
      } catch (error) {
        console.error('Error parsing user details:', error);
        setUserDetails(null);
      }
    }
  }, []);

  // Button navigation handlers
  const handleLogout = () => {
    localStorage.removeItem('userDetails');
    navigate('/logout');
  };
  const handleDeleteAccount = () => navigate('/delete-account');
  const handleAboutPage = () => navigate('/about');

  // Dynamic card data
  const cardData = [
    {
      id: 1,
      title: 'View All Clubs',
      image: '/club.jpg',
      onClick: () => navigate('/clubs'),
      btnClass: 'btn-primary',
    },
    {
      id: 2,
      title: 'View All Events',
      image: '/event.jpg',
      onClick: () => navigate('/events'),
      btnClass: 'btn-secondary',
    },
    {
      id: 3,
      title: 'View All Resources',
      image: '/re.jpg',
      onClick: () => navigate('/resources'),
      btnClass: 'btn-success',
    },
    {
      id: 4,
      title: 'Share Your Thoughts',
      image: '/share-thoughts.jpg',
      onClick: () => navigate('/share-thought'),
      btnClass: 'btn-info',
    },
  ];

  return (
    <div className="dashboard-container">
      {/* Logo */}
      <img src="/logo1.png" alt="Logo" className="logo-img" />
      <p className="logo-caption">University of Chittagong</p>

      {/* Profile Section */}
      <div className="profile-section">
        {userDetails ? (
          <div className="profile-info">
            <h2 className="username">{userDetails.name}</h2>
            <p className="user-role">{userDetails.role}</p>
          </div>
        ) : (
          <p>Loading user details...</p>
        )}

        {/* Top-Right Buttons */}
        <div className="top-right-buttons">
          <button className="btn btn-dark" onClick={handleAboutPage}>
            About
          </button>
          <button className="btn btn-danger" onClick={handleDeleteAccount}>
            Delete Account
          </button>
          <button className="btn btn-warning" onClick={handleLogout}>
            Logout
          </button>
        </div>
      </div>

      {/* Welcome Message */}
      <h1 className="dashboard-title">
        <span className="highlight">CSECU ASSOCIATION</span>
      </h1>
      <h2 className="dashboard-title1">
        Welcome to the <span className="highlight">Digital Frontier</span> – it’s time to explore
      </h2>

      {/* Dynamic Cards Section */}
      <div className="card-container">
        {cardData.map((card) => (
          <div key={card.id} className="card" onClick={card.onClick}>
            <img src={card.image} alt={card.title} className="card-img" />
            <div className="card-overlay">
              <button className={`btn ${card.btnClass}`}>{card.title}</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Dashboard;
