// AgeInput.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function AgeInput() {
  const [age, setAge] = useState('');
  const navigate = useNavigate();
  
  const handleAgeSubmit = (e) => {
    e.preventDefault();
    if (age >= 18) {
      // Navigate to the front page where user can sign up or login
      navigate('/frontpage'); // Direct to the front page
    } else {
      // Redirect to an age-restricted page or show an alert
      alert('You must be 18 or older to access this page.');
    }
  };

  return (
    <div className="age-input-container">
      <h2>Enter Your Age</h2>
      <form onSubmit={handleAgeSubmit}>
        <input 
          type="number" 
          value={age} 
          onChange={(e) => setAge(e.target.value)} 
          placeholder="Enter your age"
          required
        />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
}

export default AgeInput;
