import React, { useState } from "react";
import api from "../api"; // Axios instance

function CreateClub() {
  const [clubData, setClubData] = useState({
    club_name: "",
    description: "",
    creation_date: "",
  });

  const handleChange = (e) => {
    setClubData({ ...clubData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await api.post("/admin/create-club", clubData, {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });
      alert(response.data.message); // Show success message
    } catch (error) {
      alert(error.response?.data?.error || "Failed to create club");
    }
  };

  return (
    <div>
      <h2>Create Club</h2>
      <form onSubmit={handleSubmit}>
        <input
          name="club_name"
          type="text"
          placeholder="Club Name"
          value={clubData.club_name}
          onChange={handleChange}
        />
        <textarea
          name="description"
          placeholder="Description"
          value={clubData.description}
          onChange={handleChange}
        />
        <input
          name="creation_date"
          type="date"
          value={clubData.creation_date}
          onChange={handleChange}
        />
        <button type="submit">Create Club</button>
      </form>
    </div>
  );
}

export default CreateClub;
