import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ResourcesPage = () => {
  const [resources, setResources] = useState([]);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(true);
  const [file, setFile] = useState(null);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  // Fetch resources when the component mounts
  useEffect(() => {
    const token = localStorage.getItem('token');
    const user_id = localStorage.getItem('user_id');

    if (token && user_id) {
      axios
        .get('http://localhost:8000/resources/all', {
          headers: { Authorization: `Bearer ${token}` },
        })
        .then((response) => {
          setResources(response.data.resources);
          setLoading(false);
        })
        .catch((err) => {
          console.error('Error fetching resources:', err);
          setMessage('Error loading resources.');
          setLoading(false);
        });
    } else {
      setMessage('Access denied. Please log in.');
      setLoading(false);
    }
  }, []);

  // Handle file upload
  const handleUpload = async (e) => {
    e.preventDefault();
    const user_id = localStorage.getItem('user_id');

    if (!file || !title || !user_id) {
      alert('Please fill all fields.');
      return;
    }

    const formData = new FormData();
    formData.append('file', file);
    formData.append('title', title);
    formData.append('description', description);
    formData.append('user_id', user_id);

    try {
      const response = await axios.post('http://localhost:8000/resources/upload', formData, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      if (response.data.success) {
        setResources([...resources, response.data.resource]);
        alert('File uploaded successfully!');
      } else {
        alert('File upload failed.');
      }
    } catch (error) {
      console.error('Error uploading file:', error.message);
      alert('Error uploading file.');
    }
  };

  // Handle file download
  const handleDownload = (fileUrl) => {
    if (!fileUrl.startsWith('http')) {
      fileUrl = `http://localhost:8000/${fileUrl}`;
    }
    window.open(fileUrl, '_blank');
  };

  return (
    <div>
      <h1>All Resources</h1>

      <div>
        <h2>Upload New Resource</h2>
        <form onSubmit={handleUpload}>
          <input
            type="text"
            placeholder="Resource Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
          <textarea
            placeholder="Description (optional)"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <input
            type="file"
            onChange={(e) => setFile(e.target.files[0])}
            required
          />
          <button type="submit">Upload</button>
        </form>
      </div>

      {loading && <p>Loading resources...</p>}
      {message && <p>{message}</p>}
      <ul>
        {resources.length > 0 ? (
          resources.map((resource) => (
            <li key={resource.resource_id}>
              <strong>{resource.title}</strong>
              <p>Type: {resource.type}</p>
              <p>Uploaded on: {new Date(resource.upload_date).toLocaleString()}</p>
              <p>{resource.description}</p>
              {resource.file_url && (
                <button onClick={() => handleDownload(resource.file_url)}>Download File</button>
              )}
            </li>
          ))
        ) : (
          <p>No resources available.</p>
        )}
      </ul>
    </div>
  );
};

export default ResourcesPage;
