import React, { useEffect, useState } from 'react';
import axios from 'axios';

const ClubList = () => {
    const [clubs, setClubs] = useState([]);

    useEffect(() => {
        const fetchClubs = async () => {
            try {
                const response = await axios.get('http://localhost:8000/api/clubs');
                setClubs(response.data);
            } catch (error) {
                console.error('Error fetching clubs:', error.message);
            }
        };

        fetchClubs();
    }, []);

    return (
        <div>
            <h2>Available Clubs</h2>
            <ul>
                {clubs.map((club) => (
                    <li key={club.club_id}>
                        <h3>{club.club_name}</h3>
                        <p>{club.description}</p>
                        <JoinButton clubId={club.club_id} />
                    </li>
                ))}
            </ul>
        </div>
    );
};

const JoinButton = ({ clubId }) => {
    const handleJoin = async () => {
        try {
            const token = localStorage.getItem('token');
            await axios.post(`http://localhost:8000/api/clubs/join/${clubId}`, {}, {
                headers: { Authorization: token },
            });
            alert('You have successfully joined the club!');
        } catch (error) {
            console.error('Error joining club:', error.response?.data?.message || error.message);
        }
    };

    return <button onClick={handleJoin}>Join Club</button>;
};

export default ClubList;
