import React, { useState } from 'react';
import axios from 'axios';

const SearchTechnician = () => {
  const [location, setLocation] = useState('');
  const [technicians, setTechnicians] = useState([]);
  const [error, setError] = useState('');

  const handleSearch = async () => {
    if (!location) {
      setError('Please select a location.');
      return;
    }
    setError('');

    try {
      const response = await axios.get(`http://localhost:8000/search-technicians`, {
        params: { location },
      });
      if (response.data.success) {
        setTechnicians(response.data.technicians);
      } else {
        setError('No technicians found for the given location.');
        setTechnicians([]);
      }
    } catch (err) {
      console.error(err);
      setError('An error occurred while searching for technicians.');
      setTechnicians([]);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Search Technicians by Location</h2>

      <div style={{ marginBottom: '10px' }}>
        <label htmlFor="location" style={{ marginRight: '10px' }}>Location:</label>
        <select
          id="location"
          value={location}
          onChange={(e) => setLocation(e.target.value)}
        >
          <option value="">--Select Location--</option>
          <option value="New York">New York</option>
          <option value="Los Angeles">Los Angeles</option>
          <option value="Chicago">Chicago</option>
          <option value="San Francisco">San Francisco</option>
        </select>
      </div>

      <button onClick={handleSearch} style={{ padding: '10px 20px', marginBottom: '20px' }}>
        Search
      </button>

      {error && <p style={{ color: 'red' }}>{error}</p>}

      {technicians.length > 0 && (
        <div>
          <h3>Available Technicians:</h3>
          <ul>
            {technicians.map((tech) => (
              <li key={tech.user_id}>
                <strong>{tech.user_name}</strong> ({tech.email}, {tech.phone_number})
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default SearchTechnician;
