import React, { useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const navigate = useNavigate();
  const topRightContainerRef = useRef(null);

  useEffect(() => {
    // Dynamically position the top-right container
    const container = topRightContainerRef.current;
    if (container) {
      container.style.position = 'absolute';
      container.style.top = '20px';
      container.style.right = '20px';
      container.style.display = 'flex';
      container.style.gap = '15px';
    }
  }, []);

  const handleViewClubs = () => {
    navigate('/clubs');
  };

  const handleViewEvents = () => {
    navigate('/events');
  };

  const handleViewResources = () => {
    navigate('/resources');
  };

  const handleDeleteAccount = () => {
    navigate('/delete-account');
  };

  const handleLogout = () => {
    navigate('/logout');
  };

  return (
    <div className="dashboard-container" style={styles.dashboardContainer}>
      {/* Top-right actions */}
      <div ref={topRightContainerRef}>
        <button onClick={handleDeleteAccount} style={styles.deleteAccountButton}>
          Delete Account
        </button>
        <button onClick={handleLogout} style={styles.logoutButton}>
          Logout
        </button>
      </div>

      {/* Main Content */}
      <h1 style={styles.pageTitle}>Dashboard</h1>

      <div style={styles.buttonContainer}>
        <button onClick={handleViewClubs} style={styles.actionButton}>
          View All Clubs
        </button>
        <button onClick={handleViewEvents} style={styles.actionButton}>
          View All Events
        </button>
        <button onClick={handleViewResources} style={styles.actionButton}>
          View All Resources
        </button>
      </div>
    </div>
  );
};

// Inline styles for the dashboard
const styles = {
  dashboardContainer: {
    position: 'relative',
    padding: '40px 30px',
    backgroundColor: '#f9f9f9',
    minHeight: '100vh',
    fontFamily: "'Roboto', sans-serif",
    color: '#333',
  },
  pageTitle: {
    textAlign: 'center',
    margin: '50px 0',
    color: '#2c3e50',
    fontSize: '36px',
  },
  buttonContainer: {
    display: 'flex',
    justifyContent: 'center',
    gap: '20px',
    marginTop: '30px',
  },
  actionButton: {
    padding: '15px 30px',
    backgroundColor: '#3498db',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
    transition: 'background-color 0.3s ease',
  },
  actionButtonHover: {
    backgroundColor: '#2980b9',
  },
  deleteAccountButton: {
    padding: '12px 20px',
    backgroundColor: '#e74c3c',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
    transition: 'background-color 0.3s ease',
  },
  logoutButton: {
    padding: '12px 20px',
    backgroundColor: '#7f8c8d',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer',
    fontSize: '16px',
    transition: 'background-color 0.3s ease',
  },
};

export default Dashboard;
